INSERT INTO `afirmacje` (`id`, `problem`, `problemFormat`, `pPrzyczyna`, `nowyWzorzec`) VALUES
(71, 'Addisona Choroba (nadnercza)', 'addisona choroba (nadnercza)', 'Dotkliwe „niedożywienie\" emocjonalne. Złość na siebie.', 'Z miłością troszczę się o moje ciało, mój umysł i moje emocje.'),
(72, 'AIDS', 'aids', 'Poczucie beznadziejności i rozpaczy. „Nikt o mnie nie dba\". Silne przeświadczenie, że nie jest się dość dobrym. Wyparcie się siebie. Poczucie winy na tle seksualnym.', 'Jestem częścią Wszechświata. Jestem ważny i kochany przez samo Życie. Jestem silny i uzdolniony. Kocham siebie i wysoko siebie cenię.'),
(73, 'Aktywność nadmierna', 'aktywnosc nadmierna', 'Lęk. Uczucie przytłoczenia.', 'Jestem bezpieczny. Znika wszelkie napięcie. Jestem wystarczająco dobry.'),
(74, 'Alergia (uczulenie)', 'alergia (uczulenie)', 'Na kogo jesteś uczulony? Wypieranie się własnej mocy.', 'Świat jest bezpieczny i przyjazny. Jestem bezpieczny. Jestem w zgodzie z życiem.'),
(75, 'Alkoholizm', 'alkoholizm', '„Po co to wszystko\"? Poczucie daremności, winy, nie nadawania się.', 'Żyję w teraźniejszości. Każda chwila jest nowa. Chcę widzieć swoją wartość. Kocham i akceptuję siebie.'),
(76, 'Alzheimera choroba', 'alzheimera choroba', 'Opór przed doświadczaniem świata takim, jaki jest. Brak nadziei i bezradność. Gniew.', 'Zawsze istnieje dla mnie nowa, lepsza droga doświadczania życia. Wybaczam sobie przeszłość i uwalniam ją. Podążam naprzód ku radości.'),
(77, 'Amnezja (utrata pamięci)', 'amnezja (utrata pamieci)', 'Lęk. Ucieczka od życia. Niezdolność do obrony swojego ja.', 'Ufam swojej inteligencji i odwadze, mam poczucie własnej wartości. Bezpiecznie jest żyć.'),
(78, 'Angina, dyfteryt', 'angina, dyfteryt', 'Silne przekonanie, że nie możesz wyrażać siebie i prosić o to, czego potrzebujesz.', 'Zasługuję na to, by moje potrzeby zostały spełnione. Z miłością i bez trudu proszę o to, czego potrzebuję i dostaję to.'),
(79, 'Anemia', 'anemia', 'Postawa „tak, ale...\". Brak radości, lęk przed życiem. Poczucie, że nie jest się „dość dobrym\".', 'Bezpiecznie jest przeżywać radość w każdej dziedzinie mojego życia. Kocham życie.'),
(80, 'Anemia sierpowata', 'anemia sierpowata', '„To dziecko nie jest dość dobre\" — przekonanie, które niszczy każdą radość życia.', 'To dziecko żyje i oddycha radością życia i żywi się miłością. Bóg czyni cuda każdego dnia.'),
(81, 'Apatia', 'apatia', 'Ucieczka przed odczuwaniem. Tłumienie swojego ja. Lęk.', 'Bezpiecznie jest odczuwać. Otwieram się na życie. Chcę doświadczać życia.'),
(82, 'Apetyt — nadmierny', 'apetyt — nadmierny', 'Lęk. Potrzeba ochrony. Osądzanie swoich uczuć.', 'Jestem bezpieczny. Bezpiecznie jest czuć. Moje uczucia są normalne i godne akceptacji.'),
(83, 'Apetyt — utrata por. Brak łaknienia', 'apetyt — utrata por. brak laknienia', 'Lęk. Ochranianie siebie. Nieufność wobec życia.', 'Kocham i aprobuję siebie. Jestem bezpieczny. Życie jest bezpieczne i radosne.'),
(84, 'Arterioskleroza (miażdżyca)', 'arterioskleroza (miazdzyca)', 'Opór, napięcie. Zatwardziałość, ciasnota umysłowa. Niechęć do dostrzegania dobra.', 'Jestem zupełnie otwarty dla życia i radości. Chcę widzieć oczami miłości.'),
(85, 'Artretyzm (stawy)', 'artretyzm (stawy)', 'Poczucie bycia niekochanym. Krytycyzm, uraza.', 'Jestem miłością. Postanawiam kochać i aprobować siebie. Z miłością patrzę na innych.'),
(86, 'Astma', 'astma', 'Przytłaczająca miłość. Niezdolność do samodzielnego oddychania. Poczucie przyduszenia. Stłumiony płacz.', 'Bezpiecznie jest przyjąć odpowiedzialność za moje życie. Postanawiam być wolnym.'),
(87, 'Astma dziecięca', 'astma dziecieca', 'Lęk przed życiem. Niechęć do bycia tutaj.', 'To dziecko jest bezpieczne i kochane. To dziecko jest chciane i umiłowane.'),
(88, 'Astygmatyzm (oczy)', 'astygmatyzm (oczy)', '-', '-'),
(89, 'Ataki (agresji), wybuchy emocjonalne', 'ataki (agresji), wybuchy emocjonalne', 'Ucieczka od rodziny, od siebie lub od życia.', 'Jestem zadomowiony we Wszechświecie. Jestem bezpieczny i rozumiany.'),
(90, 'Barki (stawy, garbienie się)', 'barki (stawy, garbienie sie)', 'Reprezentują zdolność do radosnego unoszenia naszych doświadczeń. Nasza postawa wobec życia czyni z niego ciężar.', 'Postanawiam, by moje wszystkie doznania były przepełnione radością i miłością.'),
(91, 'Bezpłodność', 'bezplodnosc', 'Lęk i opór wobec biegu życia lub brak potrzeby przejścia przez doświadczenia rodzicielskie.', 'Ufam procesowi życia. Jestem zawsze na właściwym miejscu, robię właściwe rzeczy we właściwym czasie. Kocham i aprobuję siebie.'),
(92, 'Bezsenność', 'bezsennosc', 'Lęk. Nieufność wobec biegu życia. Poczucie winy.', 'Z miłością zostawiam dzień i zapadam w spokojny sen, wiedząc, że jutro samo zatroszczy się o siebie.'),
(93, 'Białaczka (krew - dolegliwości)', 'bialaczka (krew - dolegliwosci)', 'Brutalne tłumienie wdechu. „Po co to wszystko\".', 'Wykraczam poza przeszłe ograniczenia, ku obecnej wolności. Bezpiecznie jest być sobą.'),
(94, 'Biegunka (rozwolnienie)', 'biegunka (rozwolnienie)', 'Lęk i silny gniew.', 'Zaprowadzam spokój w moich myślach, a moje ciało go odzwierciedla.'),
(95, 'Biegunka amebowa', 'biegunka amebowa', 'Sądzisz, że wszystko sprzysięgło się przeciw tobie.', 'Jestem siłą i autorytetem w moim świecie. Jestem spokojny.'),
(96, 'Biegunka bakteryjna', 'biegunka bakteryjna', 'Przygnębienie, brak nadziei.', 'Jestem pełen życia, energii i radości.'),
(97, 'Bielactwo', 'bielactwo', 'Brak poczucia przynależności. Poczucie wyobcowania z grupy. Bycie na zewnątrz.', 'Jestem w samym środku Życia, całkowicie przeniknięty Miłością.'),
(98, 'Bielnica', 'bielnica', 'Uczucie skrępowania. Wielkie rozczarowanie i gniew. Wymagania i nieufność w związkach. Pasożytowanie.', 'Pozwalam sobie być wszystkim, czym mogę być i osiągam w życiu wszystko, co najlepsze. Kocham i akceptuję siebie i innych.'),
(99, 'Biodro', 'biodro', 'Utrzymuje ciało w doskonałej równowadze. Najważniejszy element poruszania się do przodu.', 'Każdy nowy dzień niesie radość. Jestem zrównoważony i swobodny.'),
(100, 'Biodro - dolegliwości', 'biodro - dolegliwosci', 'Lęk przed realizacją ważnych decyzji. Nie ma po co iść naprzód.', 'Jestem w doskonałej równowadze. W każdym wieku poruszam się naprzód lekko i radośnie.'),
(101, 'Ból (chroniczny ból)', 'bol (chroniczny bol)', 'Poczucie winy. Wina zawsze wymaga kary.', 'Z miłością uwalniam przeszłość. Inni są wolni i ja jestem wolny. Teraz w moim sercu wszystko jest w porządku.'),
(102, 'Brighta choroba (zapalenie miąższowe nerek)', 'brighta choroba (zapalenie miazszowe nerek)', 'Czucie się jak dziecko, które niczego nie potrafi robić właściwie i nie jest wystarczająco dobre. Poczucie braku.', 'Kocham i akceptuję siebie. Troszczę się o siebie. Zawsze jestem w porządku.'),
(103, 'Brodawki', 'brodawki', 'Słabe uzewnętrznianie nienawiści. Wiara w brzydotę.', 'Jestem doskonałym wyrazem miłości i piękna Życia.'),
(104, 'Cellulitis', 'cellulitis', 'Nagromadzony gniew. Karanie siebie.', 'Wybaczam innym. Wybaczam sobie. Jestem wolna, aby kochać i cieszyć się życiem.'),
(105, 'Cholesterol — nadmiar', 'cholesterol — nadmiar', 'Zablokowanie kanałów radości. Lęk przed doświadczaniem radości.', 'Postanawiam kochać życie. Moje kanały radości są szeroko otwarte. Bezpiecznie jest przyjmować.'),
(106, 'Choroby okresu dziecięcego', 'choroby okresu dzieciecego', 'Wiara w ustalony porządek, wzorce społeczne i nieprawdziwe zasady. Dziecinne zachowania dorosłych z otoczenia.', 'To dziecko jest chronione przez Boga i otoczone miłością. Domagamy się jego psychicznej nietykalności.'),
(107, 'Chrapanie', 'chrapanie', 'Uparta odmowa uwolnienia się od starych wzorców.', 'Zostawiam wszystko, co w moim umyśle nie jest miłością i radością. Przechodzę od przeszłości do tego, co nowe, świeże i żywotne.'),
(108, 'Chroniczne stany chorobowe', 'chroniczne stany chorobowe', 'Niechęć do zmiany. Strach przed przyszłością. Brak poczucia bezpieczeństwa.', 'Chcę się zmienić i rosnąć. Teraz tworzę nową i bezpieczną przyszłość.'),
(109, 'Chroniczny ból', 'chroniczny bol', 'Tęsknota za miłością. Tęsknota za oparciem.', 'Kocham i akceptuję siebie. Kocham i zasługuję na miłość.'),
(110, 'Ciśnienie krwi — nadciśnienie', 'cisnienie krwi — nadcisnienie', 'Długotrwały, nierozwiązany problem emocjonalny.', 'Z radością uwalniam się od przeszłości. Jestem spokojny.'),
(111, 'Ciśnienie krwi — niskie ciśnienie', 'cisnienie krwi — niskie cisnienie', 'Brak miłości w dzieciństwie. Defetyzm. „Po co to wszystko? To się nigdy nie uda\".', 'Chcę żyć w zawsze radosnym TERAZ. Moje życie jest radością.'),
(112, 'Cukrzyca', 'cukrzyca', 'Tęsknota za czymś, co mogłoby się wydarzyć. Wielka potrzeba sprawowania kontroli. Głęboki smutek. Brak słodyczy.', 'Ta chwila jest wypełniona radością. Decyduję się przeżyć słodycz dnia dzisiejszego.'),
(113, 'Cukrzyca — niedocukrzenie', 'cukrzyca — niedocukrzenie', 'Przytłoczenie ciężarem życia. „To i tak nie ma sensu\".', 'Postanawiam tworzyć życie pełne światła, prostoty i radości.'),
(114, 'Cuschinga choroba', 'cuschinga choroba', 'Nierównowaga psychiczna. Natłok niszczących myśli. Uczucie przytłoczenia.', 'Z miłością równoważę mój umysł i moje ciało. Wybieram myśli, które dają mi dobre samopoczucie.'),
(115, 'Cysty', 'cysty', 'Ciągłe przeżywanie starych, bolesnych doznań. Pielęgnowanie uraz. Błędnie rozumiany rozwój.', 'Moje doznania są piękne, bo tego właśnie chcę. Kocham siebie.'),
(116, 'Czyraki', 'czyraki', 'Kipienie gniewem. Wrzenie. Złość.', 'Wyrażam miłość i radość, jestem spokojny.'),
(117, 'Czyrak mnogi', 'czyrak mnogi', 'Trujący gniew z powodu osobistych krzywd.', 'Uwalniam przeszłość i pozwalam, by czas uzdrowił każdy obszar mojego życia.'),
(118, 'Depresja', 'depresja', 'Nie pozwalasz sobie na odczuwanie gniewu. Brak nadziei.', 'Wznoszę się ponad lęki i ograniczenia innych ludzi. Sam tworzę moje życie.'),
(119, 'Dłonie', 'dlonie', 'Trzymają i dotykają. Ściskają i chwytają. Zatrzymują i puszczają. Pieszczą. Szczypią. Wszystko, co dotyczy traktowania swoich doświadczeń.', 'Postanawiam przyjmować moje doświadczenia z miłością, radością i swobodą.'),
(120, 'Dreszcze', 'dreszcze', 'Psychiczne wycofanie się do wewnątrz, „Kurczenie się.\" Pragnienie ucieczki. „Zostawcie mnie w spokoju\".', 'W każdej chwili jestem bezpieczny. Miłość otacza mnie i chroni. Wszystko jest w porządku.'),
(121, 'Drogi oddechowe', 'drogi oddechowe', 'Obawa przed doświadczaniem życia w pełni.', 'Jestem bezpieczny. Kocham życie.'),
(122, 'Drogi moczowe — infekcja', 'drogi moczowe — infekcja', 'Poczucie, że jest się lekceważonym. Z reguły przez przedstawicieli płci przeciwnej lub swego partnera. Zrzucanie winy na innych.', 'Usuwam ze swojej świadomości sposób myślenia powodujący ten stan. Chcę się zmienić. Kocham i akceptuję siebie.'),
(123, 'Drogi moczowe — zapalenie cewki moczowej', 'drogi moczowe — zapalenie cewki moczowej', 'Uczucie gniewu. Wstyd.', 'W moim życiu tworzę tylko radosne doświadczenia.'),
(124, 'Drożdżyca', 'drozdzyca', 'Odrzucanie własnych potrzeb. Nie wspieranie siebie.', 'Postanawiam teraz wspierać siebie radością i miłością.'),
(125, 'Duszności', 'dusznosci', 'Lęk. Nieufność wobec biegu życia. Niechęć do bycia dorosłym.', 'Bezpiecznie jest być dorosłym. Świat jest bezpieczny. Jestem bezpieczny.'),
(126, 'Dyskopatia', 'dyskopatia', 'Uczucie braku życiowego wsparcia. Niezdecydowanie.', 'Życie wspiera wszystkie moje myśli i dlatego kocham i akceptuję siebie. Wszystko jest w porządku.'),
(127, 'Dziąsła — dolegliwości', 'dziasla — dolegliwosci', 'Nieumiejętność uzasadniania własnych decyzji. „Nie wiem, czego chcę\".', 'Jestem zdecydowany. Realizuję swoje decyzje do końca i wspieram się miłością.'),
(128, 'Dziąsła — krwawienie', 'dziasla — krwawienie', 'Brak radości z podjętych decyzji.', 'Ufam, że moje działanie jest właściwe. Jestem spokojny.'),
(129, 'Egzema', 'egzema', 'Zapierający dech konflikt. Brak równowagi psychicznej.', 'Harmonia i pokój, miłość i radość otaczają mnie i zamieszkują we mnie. Jestem bezpieczny i ochraniany.'),
(130, 'Epilepsja (padaczka)', 'epilepsja (padaczka)', 'Poczucie prześladowania. Odrzucanie życia. Ciągłe zmaganie. Gwałt zadawany samemu sobie.', 'Widzę życie jako wieczne i radosne. Jestem wieczny, radosny i spokojny.'),
(131, 'Epsteina — Barra wirus', 'epsteina — barra wirus', 'Zajadła walka z własnymi ograniczeniami. Wyczerpanie wewnętrznej siły. Wirus stresu.', 'Odprężam się i uznaję swoją wartość. Jestem wystarczająco dobry. Życie jest proste i radosne.'),
(132, 'Gangrena', 'gangrena', 'Psychiczna patologia. Radość utopiona w zatrutych myślach. Dźwiganie ciężarów życia. Poczucie bezradności i beznadziejności.', 'Dostrzegam tylko pogodne myśli i pozwalam radości swobodnie przepływać przeze mnie. Stoję prosty i wolny. Kocham i akceptuję siebie. Moje życie staje się z dnia na dzień lepsze.'),
(133, 'Gardło', 'gardlo', 'Droga ekspresji. Kanał twórczości.', 'Otwieram moje serce i chwalę radość miłości.'),
(134, 'Gardło — dolegliwości', 'gardlo — dolegliwosci', 'Niezdolność do mówienia za siebie. Zdławiony gniew. Zduszona ekspresja. Odmowa zmiany.', 'Nic nie szkodzi hałasować. Wyrażam siebie swobodnie i radośnie. Mówię za siebie z łatwością. Wyrażam swoją twórczą osobowość. Chcę się zmienić.'),
(135, 'Gardło — zapalenie', 'gardlo — zapalenie', 'Wstrzymywanie gniewnych słów. Poczucie niezdolności do wyrażania siebie.', 'Pozbywam się wszelkich ograniczeń. Wolno mi być sobą.'),
(136, 'Gardło — uczucie ucisku, „klucha w gardle\"', 'gardlo — uczucie ucisku, „klucha w gardle\"', 'Lęk. Nieuleganie biegowi życia.', 'Jestem bezpieczny. Wierzę, że Życie jest dla mnie darem. Wyrażam siebie swobodnie i radośnie.'),
(137, 'Genitalia (narządy płciowe)', 'genitalia (narzady plciowe)', 'Reprezentują męskość i kobiecość.', 'Dobrze jest być tym, kim jestem.'),
(138, 'Genitalia — problemy', 'genitalia — problemy', 'Lęk, że nie jest się dość dobrym.', 'Znajduję radość w wyrażaniu życia. Jestem doskonały, taki, jaki jestem. Kocham i akceptuję siebie.'),
(139, 'Głowa — bóle (por. migrena)', 'glowa — bole (por. migrena)', 'Niedocenianie siebie. Samokrytycyzm. Lęk.', 'Kocham i akceptuję siebie. Patrzę na siebie i na to, co robię, z miłością. Jestem bezpieczny.'),
(140, 'Głowa — zawroty', 'glowa — zawroty', 'Niestałe, rozproszone myślenie. Odmowa spojrzenia wprost na rzeczywistość.', 'Jestem skupiony i spokojny. Mogę bezpiecznie żyć i cieszyć się życiem.'),
(141, 'Głuchota', 'gluchota', 'Odrzucanie, nieustępliwość, izolacja. Czego nie chcesz słyszeć? „Dajcie mi spokój.\"', 'Przysłuchuję się temu, co Boskie i cieszę się, że jestem zdolny słyszeć. Jestem jednością ze wszystkim.'),
(142, 'Gorączka', 'goraczka', 'Gniew. Rozpalone emocje.', 'Jestem chłodnym, spokojnym wyrazem pokoju i miłości.'),
(143, 'Gościec', 'gosciec', 'Krytycyzm wobec autorytetów. Poczucie stłumienia.', 'Jestem dla siebie autorytetem. Kocham i akceptuję siebie. Życie jest dobre.'),
(144, 'Grasica', 'grasica', 'Najważniejszy gruczoł w systemie odpornościowym. Uczucie, że jest się atakowanym przez Życie. „Wszystko sprzysięgło się przeciwko mnie\".', 'Moje myśli pełne miłości wzmacniają mój system odpornościowy. Jestem bezpieczny wewnątrz i na zewnątrz. Wsłuchuję się w siebie z miłością.'),
(145, 'Gruczolistność wewnętrzna (endometrioza)', 'gruczolistnosc wewnetrzna (endometrioza)', 'Brak poczucia bezpieczeństwa. Rozczarowanie i frustracja. Słodycze zastępują miłość do siebie.', 'Czuję swoją siłę i atrakcyjność. To cudowne być kobietą. Kocham siebie i jestem spełniona.'),
(146, 'Gruczoły dokrewne (węzły chłonne)', 'gruczoly dokrewne (wezly chlonne)', 'Symbolizują gotowość do działania, samoistną aktywność.', 'Jestem twórczą siłą w moim świecie.'),
(147, 'Gruczoły dokrewne — dolegliwości', 'gruczoly dokrewne — dolegliwosci', 'Zbyt mało pomysłów. Powstrzymywanie własnej aktywności.', 'Mam do dyspozycji wszystkie Boskie pomysły i sposoby działania, jakich potrzebuję. Od tej chwili zaczynam działać.'),
(148, 'Gruźlica', 'gruzlica', 'Zaborczość. Okrutne myśli. Chęć zemsty. Wyniszczanie siebie samolubstwem.', 'Tak jak kocham i akceptuję siebie, tak tworzę radosny i spokojny świat, w którym żyję.'),
(149, 'Grypa', 'grypa', 'Odpowiedź na powszechne negatywne nastawienia i przekonania. Strach. Wiara w statystyczne uogólnienia.', 'Jestem ponad powszechne przekonania i wiarę w „stosowną porę\". Jestem wolny od wszelkich wpływów.'),
(150, 'Grzybica', 'grzybica', 'Pozwalasz innym, by załazili ci za skórę. Nie czujesz się dość dobry ani czysty.', 'Kocham i akceptuję siebie. Nikt, żadne miejsce, żadna rzecz nie mają nade mną władzy. Jestem wolny.'),
(151, 'Guzki, małe zgrubienia', 'guzki, male zgrubienia', 'Uraza i frustracja, zranione ego.', 'Odrzucam swój wzorzec powstrzymywania i pozwalam sobie odnieść sukces.'),
(152, 'Guzy (por. narośl)', 'guzy (por. narosl)', 'Pielęgnowanie starych ran i wstrząsów. Narastanie wyrzutów sumienia.', 'Z miłością uwalniam przeszłość i zwracam moją uwagę na dzień dzisiejszy. Wszystko jest w porządku.'),
(153, 'Hemoroidy', 'hemoroidy', '(Patrz: odbyt)', '-'),
(154, 'Hiperglikemia', 'hiperglikemia', '(Patrz: cukrzyca)', '-'),
(155, 'Hiperwentylacja', 'hiperwentylacja', 'Lęk. Opieranie się zmianom. Nieufność wobec życia.', 'Jestem bezpieczny w każdym miejscu Wszechświata. Kocham siebie i ufam biegowi życia.'),
(156, 'Choroba Hodgkina (ziarnica złośliwa, limfogranulomatoza)', 'choroba hodgkina (ziarnica zlosliwa, limfogranulomatoza)', 'Poczucie winy i silny lęk przed tym, że nie jest się „dość dobrym\". Obłędna pogoń za tym, aby dowieść swojej wartości aż do wyczerpania żywotnych sił. Radość życia zostaje zapomniana w pogoni za byciem akceptowanym.', 'Jestem absolutnie szczęśliwy będąc sobą. Jestem dobry taki, jaki jestem. Kocham i akceptuję siebie. Jestem radością wyrażającą i odczuwającą.'),
(157, 'Impotencja', 'impotencja', 'Seksualna presja, napięcie, wina. Przesądy. Niechęć do poprzedniego partnera. Strach przed matką.', 'Pozwalam, aby moja seksualność przejawiła się z całą mocą, łatwo i radośnie.'),
(158, 'Infekcje, zakażenia', 'infekcje, zakazenia', 'Rozdrażnienie, gniew, irytacja.', 'Chcę żyć w spokoju i harmonii.'),
(159, 'Ischias, rwa kulszowa', 'ischias, rwa kulszowa', 'Obłuda. Obawa o pieniądze, lęk przed przyszłością.', 'Zmierzam ku mojemu największemu dobru. Moje dobro jest wszędzie. Jestem bezpieczny i chroniony.'),
(160, 'Jadłowstręt, anoreksja', 'jadlowstret, anoreksja', '(Patrz: łaknienia brak)', '-'),
(161, 'Jajniki', 'jajniki', 'Symbolizują istotę tworzenia. Twórczość.', 'Zachowuję równowagę w przepływie mojej mocy twórczej.'),
(162, 'Jądra', 'jadra', 'Symbolizują męskość.', 'Bezpiecznie jest być mężczyzną.'),
(163, 'Jąkanie się', 'jakanie sie', 'Brak poczucia bezpieczeństwa. Nieumiejętność wyrażania siebie. Brak zgody na płacz.', 'Wolno mi mówić we własnym imieniu. Jestem całkowicie bezpieczny wyrażając siebie. Komunikuję się pełen miłości.'),
(164, 'Jelita', 'jelita', 'Symbolizują wyzwolenie od tego, co niepotrzebne.', 'Proces wyzwalania się jest łatwy.'),
(165, 'Jelita — dolegliwości', 'jelita — dolegliwosci', 'Lęk przed uwolnieniem tego, co stare i niepotrzebne.', 'Swobodnie i łatwo uwalniam się od tego, co stare i z radością witam nowe.'),
(166, 'Jelito grube, śluzowatość', 'jelito grube, sluzowatosc', 'Nagromadzenie starych, chaotycznych myśli, zamykające kanał usuwania tego, co zbędne. Pławienie się w grząskim błocie przeszłości.', 'Uwalniam i unieważniam przeszłość. Myślę jasno, żyję teraźniejszością w spokoju i radości.'),
(167, 'Jelito grube, zapalenie', 'jelito grube, zapalenie', 'Lęk. Zmartwienia. Poczucie, że nie jest się dość dobrym.', 'Kocham i akceptuję siebie. Robię wszystko najlepiej, jak potrafię. Jestem wspaniały. Jestem spokojny.'),
(168, 'Jęczmień', 'jeczmien', '(Patrz: oczy)', '-'),
(169, 'Język', 'jezyk', 'Reprezentuje zdolność radosnego smakowania przyjemności życia.', 'Cieszę się obfitymi darami życia.'),
(170, 'Kamica nerkowa', 'kamica nerkowa', '„Kawałki\" nieujawnionego gniewu.', 'Z łatwością rozwiązuję wszystkie problemy z przeszłości.'),
(171, 'Kamienie żółciowe', 'kamienie zolciowe', 'Rozgoryczenie. Złe myśli. Pogarda. Pycha.', 'Radosne jest uwolnienie się od przeszłości. Rozkosznie jest żyć.'),
(172, 'Kaszel', 'kaszel', 'Chęć zwrócenia na siebie uwagi. „Patrzcie na mnie, posłuchajcie mnie\".', 'Jestem zauważany i wysoko ceniony. Jestem kochany.'),
(173, 'Katar sienny', 'katar sienny', 'Przeciążenie emocjonalne. Lęk przed upływem czasu. Przekonanie, że jest się prześladowanym. Poczucie winy.', 'Stanowię jedność z CAŁOŚCIĄ ŻYCIA. W każdej chwili jestem bezpieczny.'),
(174, 'Kiła', 'kila', 'Odrzucanie swojej mocy i skuteczności.', 'Postanawiam być sobą. Zgadzam się być taki, jaki jestem.'),
(175, 'Klimakterium', 'klimakterium', '(Patrz: menopauza)', '-'),
(176, 'Kobiece dolegliwości', 'kobiece dolegliwosci', 'Zaparcie się samej siebie. Odrzucanie kobiecości. Odrzucanie samej istoty bycia kobietą.', 'Zgadzam się na moją kobiecość. Kocham bycie kobietą. Kocham moje ciało.'),
(177, 'Kolano por stawy', 'kolano por stawy', 'Reprezentują dumę i ego.', 'Jestem giętki i ruchomy.'),
(178, 'Kolano — dolegliwości', 'kolano — dolegliwosci', 'Uparte ego i duma. Niezdolność, by czasem się ugiąć. Brak giętkości. Nie popuszczę.', 'Przebaczenie. Zrozumienie. Współczucie. Z łatwością przystosowuję się i poddaję. Wszystko jest dobrze.'),
(179, 'Kolka brzuszna', 'kolka brzuszna', 'Poirytowanie, niecierpliwość, nerwowość otoczenia.', 'To dziecko wzbudza tylko miłość i życzliwe myśli. Jest spokojne.'),
(180, 'Kolka jelitowa', 'kolka jelitowa', 'Lęk przed uwolnieniem. Niepewność.', 'Życie jest dla mnie dobre i zawsze troszczy się o mnie. Wszystko jest dobrze.'),
(181, 'Kostki u nóg', 'kostki u nog', 'Symbolizują umiejętność przeżywania przyjemności. Dolegliwości — brak elastyczności i poczucie winy.', 'Zasługuję na to, by cieszyć się życiem. Akceptuję wszystkie radości, jakie ofiarowuje mi życie.'),
(182, 'Kości por szkielet', 'kosci por szkielet', 'Symbolizują budowę wszechświata.', 'Jestem dobrze zbudowany i zrównoważony.'),
(183, 'Kości — deformacje', 'kosci — deformacje', 'Psychiczna presja i napięcie. Mięśnie nie mogą się rozciągać. Utrata psychicznej ruchliwości.', 'Wdycham życie pełną piersią. Jestem zrelaksowany i ufam biegowi życia.'),
(184, 'Kości — odwapnienie', 'kosci — odwapnienie', 'Poczucie braku oparcia w życiu.', 'Sam jestem dla siebie oparciem, a Życie wspiera mnie w nieoczekiwany, pełen miłości sposób.'),
(185, 'Kości — zapalenie szpiku', 'kosci — zapalenie szpiku', 'Frustracja i gniew na samą strukturę życia. Brak oparcia.', 'Jestem spokojny wobec biegu życia i ufam mu. Jestem bezpieczny i chroniony.'),
(186, 'Kości — złamania', 'kosci — zlamania', 'Bunt przeciwko autorytetom.', 'W moim świecie jestem swoim własnym autorytetem, bo jestem jedynym, który tworzy moje myśli.'),
(187, 'Kości — zrosty', 'kosci — zrosty', 'Stwardniałe pojęcia i idee, stężały strach.', 'Bezpiecznie jest widzieć i przeżywać nowe pomysły i możliwości. Jestem otwarty i chłonny wobec dobra.'),
(188, 'Krążenie', 'krazenie', 'Symbolizuje zdolność przeżywania i wyrażania emocji w sposób pozytywny.', 'Jestem wolny i wprowadzam w każdą część mojego świata miłość i radość. Kocham życie.'),
(189, 'Krew', 'krew', 'Symbolizuje radość płynącą swobodnie przez ciało.', 'Jestem wyrażającą i przyjmującą radością życia.'),
(190, 'Krew — dolegliwości', 'krew — dolegliwosci', 'Brak radości. Brak nowych koncepcji.', 'Radosne, nowe pomysły krążą we mnie swobodnie.'),
(191, 'Krew — krzepliwość', 'krew — krzepliwosc', 'Zamykanie przepływu radości.', 'Budzę w sobie nowe życie. Płynę.'),
(192, 'Krew — niedokrwistość', 'krew — niedokrwistosc', 'Przekonanie, które niszczy każdą radość życia: „Nie jestem wystarczająco dobry\".', 'To dziecko karmione jest miłością, a żyje i oddycha radością życia. Bóg czyni cuda każdego dnia.'),
(193, 'Kręgosłup', 'kregoslup', 'Elastyczna podpora życia.', 'Życie wspiera mnie.'),
(194, 'Krosty', 'krosty', '(Patrz: wągry)', '-'),
(195, 'Krtań', 'krtan', 'Droga i ujście ekspresji, kanał siły twórczej.', 'Otwieram serce i śpiewam radość miłości.'),
(196, 'Krtań — zapalenie', 'krtan — zapalenie', 'Tak wściekły, że aż nie możesz mówić. Strach przed wypowiedzeniem tego, co się myśli. Uraza wobec autorytetów.', 'Jestem wolny, by prosić o cokolwiek zechcę. Bezpiecznie jest wyrażać siebie. Jestem spokojny.'),
(197, 'Krup (dławiec)', 'krup (dlawiec)', '(Patrz: oskrzeli zapalenie)', '-'),
(198, 'Krwawienie', 'krwawienie', 'Wyciekająca radość. Gniew.', 'Jestem radością Życia, wyrażającą i przyjmującą w doskonałym rytmie.'),
(199, 'Krzywica', 'krzywica', 'Głód emocjonalny. Brak miłości i bezpieczeństwa.', 'Jestem bezpieczny i karmiony miłością Wszechświata.'),
(200, 'Kurcze (w brzuchu)', 'kurcze (w brzuchu)', 'Lęk. Powstrzymywanie.', 'Ufam biegowi życia. Jestem bezpieczny.'),
(201, 'Kurzajka', 'kurzajka', 'Gniew u podstaw twojego rozumienia. Rosnący niepokój o przyszłość.', 'Idę naprzód ufnie i z łatwością. Ufam i ulegam biegowi życia.'),
(202, 'Lewa strona ciała', 'lewa strona ciala', 'Symbolizuje wrażliwość, przyjmowanie, energię żeńską, kobiecość, matkę.', 'Moja żeńska energia jest wspaniale zrównoważona.'),
(203, 'Lęki, niepokój', 'leki, niepokoj', 'Nie ufam biegowi życia.', 'Kocham i aprobuję siebie i ufam biegowi życia. Jestem bezpieczny.'),
(204, 'Limfatyczny układ — dolegliwości', 'limfatyczny uklad — dolegliwosci', 'Ostrzeżenie, że umysł należy z powrotem ześrodkować na niezbędnych, zasadniczych elementach życia: miłości i radości.', 'Jestem teraz całkowicie skupiony na miłości i radości bycia żywym. Płynę z życiem. Włada mną spokój.'),
(205, 'Liszaj', 'liszaj', '(Patrz: toczeń rumieniowaty)', '-'),
(206, 'Lokomocyjna choroba', 'lokomocyjna choroba', 'Strach. Lęk przed utratą kontroli nad sobą.', 'Kontroluję moje myśli. Jestem bezpieczny. Kocham i akceptuję siebie.'),
(207, 'Lokomocyjna choroba — w samochodzie', 'lokomocyjna choroba — w samochodzie', 'Lęk. Uwiązanie. Poczucie uwięzienia.', 'Lekko przemieszczam się w czasie i przestrzeni. Otacza mnie miłość.'),
(208, 'Lokomocyjna choroba — na morzu', 'lokomocyjna choroba — na morzu', 'Lęk. Lęk przed śmiercią. Utrata panowania nad sobą.', 'Jestem całkiem bezpieczny we Wszechświecie. Wszędzie jestem spokojny. Ufam życiu.'),
(209, 'Łaknienia brak', 'laknienia brak', 'Odmawianie sobie życia. Silny lęk, nienawiść do siebie i odrzucenie siebie.', 'Bezpiecznie jest być sobą. Jestem cudowny taki, jaki jestem. Wybieram życie. Wybieram radość i zgodę na siebie.'),
(210, 'Łokieć por stawy', 'lokiec por stawy', 'Symbolizuje zmieniające się kierunki i akceptowanie nowych doświadczeń.', 'Z łatwością płynę wraz z nowymi doświadczeniami, kierunkami i zmianami.'),
(211, 'Łonowa kość', 'lonowa kosc', 'Symbolizuje ochronę narządów płciowych.', 'Moja płciowość jest bezpieczna.'),
(212, 'Łuszczyca', 'luszczyca', 'Lęk przed doznawaniem krzywdy. Tłumienie świadomości własnego ja. Odmowa zaakceptowania odpowiedzialności za własne uczucia.', 'Jestem otwarty na radość życia. Zasługuję i akceptuję wszystko to, co najlepsze w życiu. Kocham i akceptuję siebie.'),
(213, 'Macica', 'macica', 'Symbolizuje schronienie i kolebkę tworzenia.', 'Moje ciało jest moim schronieniem.'),
(214, 'Malaria', 'malaria', 'Zaburzona równowaga związku z przyrodą i życiem.', 'Jestem w jedności i harmonii z całym życiem. Jestem bezpieczny.'),
(215, 'Menopauza', 'menopauza', 'Lęk przed staniem się niechcianą. Lęk przed starzeniem się. Odrzucanie siebie. Poczucie, że nie jest się „dość dobrą\".', 'Jestem zrównoważona i spokojna w każdej zmianie cyklu. Błogosławię moje ciało z miłością.'),
(216, 'Miażdżyca tętnic', 'miazdzyca tetnic', '(Patrz: arterioskleroza)', '-'),
(217, 'Miedniczki nerkowe — zapalenie', 'miedniczki nerkowe — zapalenie', '(Patrz: drogi moczowe)', '-'),
(218, 'Miesiączkowanie — dolegliwości', 'miesiaczkowanie — dolegliwosci', 'Odrzucenie własnej kobiecości. Poczucie winy. Przekonanie o grzeszności czy nieczystości narządów płciowych.', 'Akceptuję całą swoją kobiecą siłę i przyjmuję wszystkie procesy zachodzące w moim ciele jako normalne i naturalne. Kocham siebie.'),
(219, 'Miesiączkowanie — bolesna por kobiece dolegliwości', 'miesiaczkowanie — bolesna por kobiece dolegliwosci', 'Gniew na samą siebie. Nienawiść do ciała lub kobiet.', 'Kocham moje ciało. Kocham siebie. Kocham każdy mój cykl. Wszystko jest w porządku.'),
(220, 'Miesiączkowanie — brak', 'miesiaczkowanie — brak', 'Niechęć do bycia kobietą. Brak akceptacji siebie.', 'Cieszę się, że jestem tą, którą jestem. Jestem pięknym wyrazem życia i w doskonały sposób podążam wraz z nim.'),
(221, 'Mięśniaki, cysty', 'miesniaki, cysty', 'Pielęgnowanie urazy spowodowanej przez partnera. Zranienie kobiecego ego.', 'Odrzucam wzorzec, który spowodował takie doświadczenie. W moim życiu tworzę tylko dobro.'),
(222, 'Mięśnie', 'miesnie', 'Opór wobec nowych doświadczeń. Mięśnie symbolizują naszą umiejętność aktywnego życia.', 'Doświadczam życia jak radosnego tańca.'),
(223, 'Mięśnie — zwyrodnienie (dystrofia)', 'miesnie — zwyrodnienie (dystrofia)', '„Nie warto dorastać\".', 'Wyrastam ponad moich rodziców. Wolno mi być sobą, tak bardzo, jak tylko potrafię.'),
(224, 'Mięśnie — zanik', 'miesnie — zanik', 'Nieakceptowanie własnej wartości. Wyrzeczenie się sukcesu.', 'Wiem, jak jestem cenny. Bezpiecznie jest odnosić sukcesy. Kocham życie.'),
(225, 'Migdałki — zapalenie', 'migdalki — zapalenie', 'Strach. Stłumione uczucia. Zduszona potrzeba tworzenia.', 'To, co we mnie dobre, swobodnie przepływa przez mnie. Boskie idee wyrażają się przeze mnie. Jestem spokojny.'),
(226, 'Migrena por ból głowy', 'migrena por bol glowy', 'Niechęć do bycia kierowanym. Opieranie się przepływowi życia. Lęki seksualne (mogą być łagodzone przez masturbację).', 'Odprężam się i pozwalam, by życie dostarczało mi wszystkiego, czego potrzebuję, łatwo i bez trosk. Życie jest dla mnie.'),
(227, 'Moczenie nocne', 'moczenie nocne', 'Lęk przed rodzicami, zwykle przed ojcem.', 'Traktujemy to dziecko z miłością, współczuciem i zrozumieniem. Wszystko jest dobrze.'),
(228, 'Mononukleoza', 'mononukleoza', 'Wściekłość na brak miłości i uznania. Zaprzestanie troski o siebie.', 'Kocham, akceptuję i troszczę się o siebie. Jestem wystarczająco dobry.'),
(229, 'Morska choroba (lokomocyjna choroba)', 'morska choroba (lokomocyjna choroba)', 'Strach przed utratą kontroli nad ciałem.', 'Z łatwością radzę sobie z ruchem i podróżami.'),
(230, 'Mózg', 'mozg', 'Symbolizuje „centrum dowodzenia\", nasza „deska rozdzielcza\".', 'Jestem pełnym miłości użytkownikiem swojego umysłu.'),
(231, 'Mózg - Porażenie', 'mozg - porazenie', 'Potrzeba zjednoczenia rodziny w czynie miłości.', 'Przyczyniam się do zgodnego, pełnego miłości i spokoju życia rodzinnego.'),
(232, 'Mózg - Udar', 'mozg - udar', 'Odmowa zmiany starych wzorców.', 'Jest mi łatwo przeprogramować komputer mojego umysłu. Życie jest zmianą.'),
(233, 'Nadczynność tarczycy', 'nadczynnosc tarczycy', 'Wściekłość, że zostało się pominiętym.', 'Jestem w centrum życia, akceptuję siebie i wszystko dokoła.'),
(234, 'Nadnercza — dolegliwości por.\nAddisona ch.,\nCushinga ch.', 'nadnercza — dolegliwosci por.\naddisona ch.,\ncushinga ch.', 'Defetyzm, zaprzestanie troski o siebie.', 'Kocham i akceptuję siebie. Bezpiecznie jest troszczyć się o siebie.'),
(235, 'Nadwaga por.\ntłuszczowa tkanka', 'nadwaga por.\ntluszczowa tkanka', 'Lęk, potrzeba opieki, ucieczka od uczuć.', 'Jestem w zgodzie z moimi uczuciami. Jestem bezpieczny tu, gdzie jestem.'),
(236, 'Nałogi', 'nalogi', 'Ucieczka od siebie, lęk.', 'Odkrywam swoje piękno. Chcę kochać siebie i cieszyć się sobą.'),
(237, 'Narkolepsja (senność napadowa)', 'narkolepsja (sennosc napadowa)', 'Strach przed porażką, chęć ucieczki od wszystkiego.', 'Ufam, że boska mądrość nieustannie mnie ochrania.'),
(238, 'Naroślą', 'narosla', 'Pielęgnowanie starych ran, obrażanie się.', 'Łatwo przebaczam. Kocham siebie i wynagradzam siebie pochwalnymi myślami.'),
(239, 'Nerki — zapalenie por. Brighta ch.', 'nerki — zapalenie por. brighta ch.', 'Przesadna reakcja na rozczarowanie i niepowodzenie.', 'Tylko właściwe działanie ma miejsce w moim życiu.'),
(240, 'Nerwoból (neuralgia)', 'nerwobol (neuralgia)', 'Karanie za winę, udręka z powodu niekomunikatywności.', 'Wybaczam sobie. Kocham i akceptuję siebie. Porozumiewam się pełen miłości.'),
(241, 'Nerwowe załamanie', 'nerwowe zalamanie', 'Zagłuszanie kanałów komunikacyjnych.', 'Otwieram moje serce i porozumiewam się pełen miłości.'),
(242, 'Nerwowość', 'nerwowosc', 'Strach, niepokój, pośpiech.', 'Jestem w bezkresnej podróży przez wieczność, mam mnóstwo czasu.'),
(243, 'Nerwy', 'nerwy', 'Symbolizują komunikację, wrażliwi sprawozdawcy.', 'Porozumiewam się łatwo i z radością.'),
(244, 'Niedoczynność tarczycy por.\ntarczyca', 'niedoczynnosc tarczycy por.\ntarczyca', 'Rezygnacja, poczucie beznadziejnego zdławienia.', 'Tworzę nowe życie i nowe wspierające mnie zasady.'),
(245, 'Niekontrolowanie odruchów wydalania', 'niekontrolowanie odruchow wydalania', 'Tłumienie uczuć przez długi czas.', 'Zgadzam się przeżywać emocje. Bezpiecznie jest wyrażać swoje uczucia.'),
(246, 'Niemiła woń ciała', 'niemila won ciala', 'Strach przed innymi ludźmi, niechęć do samego siebie.', 'Kocham i akceptuję siebie. Jestem bezpieczny.'),
(247, 'Niestrawność', 'niestrawnosc', 'Strach sięgający trzewi, przerażenie, niepokój.', 'Przyjmuję wszystkie nowe doświadczenia spokojnie i z radością.'),
(248, 'Nieuleczalna choroba', 'nieuleczalna choroba', 'Choroba, która nie może być leczona z zewnątrz. Aby leczyć, trzeba zwrócić się ku sobie.', 'Cuda zdarzają się codziennie. Wchodzę w siebie, by pozbyć się wzorca, który stworzył chorobę. Teraz przyjmuję boskie\nuzdrowienie. I oto jest!'),
(249, 'Nieświeży oddech (por oddech)', 'nieswiezy oddech (por oddech)', 'Gniew i myśl o zemście.', 'Uwalniam przeszłość z miłością. Daję wyraz tylko miłości.'),
(250, 'Nogi', 'nogi', 'Niosą nas przez życie.', 'Życie jest dla mnie.'),
(251, 'Nogi - dolegliwości\npartii dolnych', 'nogi - dolegliwosci\npartii dolnych', 'Lęk przed przyszłością. Niechęć do\npójścia naprzód.', 'Kroczę do przodu z pewnością i radością\nwiedząc, że w przyszłości wszystko będzie\ndobrze.'),
(252, 'Nogi - Piszczelowa kość goleń', 'nogi - piszczelowa kosc golen', 'Załamanie idei, kryteria, którymi kierujemy się w życiu.', 'W miłości i radości żyję zgodnie z najwyższymi wartościami.'),
(253, 'Nos', 'nos', 'Symbolizuje znajomość siebie.', 'Uznaję moje zdolności intuicyjne.'),
(254, 'Krwawienie z nosa', 'krwawienie z nosa', 'Potrzeba uznania, poczucie, że jest się na uboczu. Wołanie o miłość.', 'Kocham i akceptuję siebie. Dostrzegam moją prawdziwą wartość. Jestem cudowny.'),
(255, 'Kapanie z nosa', 'kapanie z nosa', 'Wewnętrzny płacz, prośba o pomoc.', 'Kocham siebie i w przyjemny sposób potrafię siebie pocieszyć.'),
(256, 'Zatkany nos', 'zatkany nos', 'Nieświadomość własnej wartości.', 'Kocham i cenię siebie.'),
(257, 'Nudności', 'nudnosci', 'Lęk, odrzucenie myśli lub doświadczenia.', 'Jestem bezpieczny. Ufam, że życie przynosi mi jedynie dobro.'),
(258, 'Obłęd', 'obled', 'p. psychiczna ch.', ''),
(259, 'Oczy', 'oczy', 'Symbolizują zdolność widzenia przeszłości, teraźniejszości, przyszłości.', 'Patrzę z miłością i radością.'),
(260, 'Oczy - Astygmatyzm', 'oczy - astygmatyzm', 'Kłopoty z własnym „ja\", lęk przed ujrzeniem siebie naprawdę.', 'Chcę widzieć swoje własne piękno i wspaniałość.'),
(261, 'Oczy - Dalekowzroczność', 'oczy - dalekowzrocznosc', 'Lęk przed teraźniejszością.', 'Jestem bezpieczny tu i teraz. Widzę to.'),
(262, 'Oczy - Dolegliwości oczu', 'oczy - dolegliwosci oczu', 'Niezadowolenie z tego, co widzisz w swoim życiu.', 'Tworzę życie, na które kocham patrzeć.'),
(263, 'Oczy - Jaskra', 'oczy - jaskra', 'Odmowa wybaczenia, napięcie z powodu urazów.', 'Patrzę z miłością i czułością.'),
(264, 'Oczy - Jęczmień', 'oczy - jeczmien', 'Patrzenie na życie z gniewem, złość na kogoś.', 'Chcę patrzeć na wszystkich i wszystko z radością i miłością.'),
(265, 'Oczy - Katarakta (zaćma)', 'oczy - katarakta (zacma)', 'Niezdolność do patrzenia w przyszłość z radością.', 'Życie jest wieczne i wypełnione radością.'),
(266, 'Oczy - Krótkowzroczność', 'oczy - krotkowzrocznosc', 'Lęk przed przyszłością, brak zaufania do tego, co będzie.', 'Przyjmuję Boskie przewodnictwo i zawsze jestem bezpieczny.'),
(267, 'Oczy - Suchość oczu', 'oczy - suchosc oczu', 'Gniewne oczy, odmowa patrzenia z miłością.', 'Chętnie wybaczam. Moim spojrzeniem chłonę życie z współczuciem i zrozumieniem.'),
(268, 'Oczy - Zaburzenia widzenia u dzieci', 'oczy - zaburzenia widzenia u dzieci', 'Niechęć do widzenia, co dzieje się w rodzinie.', 'Harmonia, radość, piękno i bezpieczeństwo otaczają teraz to dziecko.'),
(269, 'Oczy - Zaczerwienione oczy', 'oczy - zaczerwienione oczy', 'Gniew, napięcie, odmowa widzenia.', 'Kocham i akceptuję siebie.'),
(270, 'Oczy - Zapalenie rogówki', 'oczy - zapalenie rogowki', 'Krańcowy gniew. Pragnienie, by uderzyć kogoś lub coś w zasięgu wzroku.', 'Pozwalam miłości z mojego serca uzdrawiać wszystko, co widzę. Pragnę pokoju. Wszystko jest w porządku w moim świecie.'),
(271, 'Oczy - Zapalenie spojówek', 'oczy - zapalenie spojowek', 'Gniew i frustracja wobec tego, na co patrzysz w życiu.', 'Widzę oczami miłości. Istnieje harmonijne rozwiązanie i teraz je przyjmuję.'),
(272, 'Oczy - Zez rozbieżny', 'oczy - zez rozbiezny', 'Obawa spojrzenia na to, co jest tu i teraz.', 'Kocham i akceptuję siebie właśnie teraz.'),
(273, 'Oczy - Zez zbieżny', 'oczy - zez zbiezny', 'Niechęć zobaczenia tego, co jest na zewnątrz. Sprzeczne zamiary.', 'Bezpiecznie jest widzieć. Jestem spokojny.'),
(274, 'Odbijanie się', 'odbijanie sie', 'Lęk. Przełykanie życia zbyt szybko.', 'Jest czas i miejsce na wszystko, co potrzebuję zrobić. Jestem spokojny.'),
(275, 'Odbyt', 'odbyt', 'Miejsce uwalniania. Śmietnisko.', 'Łatwo i spokojnie uwalniam się od tego, czego już dłużej nie potrzebuję w życiu.'),
(276, 'Odbyt - Ból', 'odbyt - bol', 'Poczucie winy. Pragnienie kary. Przekonanie, że nie jest się wystarczająco dobrym.', 'Przeszłość jest zakończona. Chcę kochać i aprobować siebie w teraźniejszości.'),
(277, 'Odbyt - Hemoroidy', 'odbyt - hemoroidy', 'Lęk przed śmiercią. Gniew na przeszłość. Obawa przed wydalaniem. Poczucie nadmiernego obarczenia.', 'Uwalniam wszystko, co nie jest miłością. Jest czas i miejsce na wszystko, co chcę robić.'),
(278, 'Odbyt - Krwawienie', 'odbyt - krwawienie', 'Gniew i frustracja.', 'Ufam biegowi życia. W moim życiu ma miejsce jedynie dobre i właściwe działanie.'),
(279, 'Odbyt - Owrzodzenie', 'odbyt - owrzodzenie', 'Gniew na to, czego nie chcesz się pozbyć.', 'Bezpiecznie jest to uwolnić. Tylko to, czego już nie potrzebuję, opuszcza moje ciało.'),
(280, 'Odbyt - Przetoka', 'odbyt - przetoka', 'Niezupełne uwolnienie odpadków. Kurczowe trzymanie się śmieci z przeszłości.', 'Z miłością uwalniam przeszłość. Jestem wolny. Jestem miłością.'),
(281, 'Odbyt - Swędzenie', 'odbyt - swedzenie', 'Poczucie winy dotyczące przeszłości. Wyrzuty sumienia.', 'Z miłością wybaczam sobie. Jestem wolny.'),
(282, 'Odciski, nagniotki', 'odciski, nagniotki', 'Stwardniałe obszary myślenia — uparte trzymanie się dawnego bólu.', 'Kroczę naprzód wolny od przeszłości. Jestem bezpieczny i wolny.'),
(283, 'Oddech', 'oddech', 'Symbolizuje zdolność przyjmowania życia.', 'Kocham życie. Bezpiecznie jest żyć.'),
(284, 'Oddech - Cuchnący oddech (nieświeży oddech)', 'oddech - cuchnacy oddech (nieswiezy oddech)', 'Fałszywa postawa, podłe obgadywanie, plugawe myśli.', 'Mówię łagodnie i z miłością. Wydycham tylko to, co dobre.'),
(285, 'Oddech - Duszności, hiperwentylacja', 'oddech - dusznosci, hiperwentylacja', 'Lęk lub odmowa przeżywania życia w pełni. Zaprzeczanie prawu do własnej przestrzeni.', 'Moim naturalnym prawem jest żyć w pełni i swobodnie. Jestem wart miłości. Chcę żyć pełnią życia.'),
(286, 'Odrętwienie', 'odretwienie', 'Tłumienie miłości. Psychiczne zamieranie.', 'Dzielę się moimi uczuciami i moją miłością. Inni reagują miłością wobec mnie.'),
(287, 'Omdlenie, zasłabnięcie', 'omdlenie, zaslabniecie', 'Lęk. Nieporadność. Zaciemnianie.', 'Mam wystarczającą moc, siłę i wiedzę, by poradzić sobie ze wszystkim w życiu.'),
(288, 'Oparzenia', 'oparzenia', 'Palący gniew. Rozdrażnienie.', 'Wewnątrz i wokół siebie tworzę tylko spokój i harmonię. Zasługuję na to, by czuć się dobrze.'),
(289, 'Opryszczka (narządów płciowych) por.\nweneryczne\nchoroby', 'opryszczka (narzadow plciowych) por.\nweneryczne\nchoroby', 'Wiara w seksualną winę i potrzeba poniesienia kary. Wstyd. Wiara w karzącego Boga. Odrzucenie własnej płciowości.', 'Wspiera mnie moje pojmowanie Boga. Jestem normalny i naturalny. Cieszę się moją seksualnością i moim ciałem. Jestem cudowny.'),
(290, 'Opryszczka zwykła (wargowa) por.\nowrzodzenie jamy\nustnej', 'opryszczka zwykla (wargowa) por.\nowrzodzenie jamy\nustnej', 'Paląca złość. Powstrzymywanie gorzkich słów.', 'Myślę i wypowiadam tylko słowa miłości. Jestem w zgodzie z życiem.'),
(291, 'Opuchlizna (por puchlina wodna)', 'opuchlizna (por puchlina wodna)', 'Zawężony sposób myślenia. Pogmatwane bolesne myśli.', 'Moje myśli przepływają swobodnie i lekko. Poruszam się wśród nich z łatwością.'),
(292, 'Zapalenie oskrzeli (por bronchit)', 'zapalenie oskrzeli (por bronchit)', '„Zapalne\" ognisko w otoczeniu rodzinnym. Kłótnie i krzyki. Okresy „cichych dni\".', 'Ogłaszam pokój i zgodę we mnie i dookoła mnie. Wszystko jest dobrze.'),
(293, 'Osłabienie', 'oslabienie', 'Potrzeba psychicznego odpoczynku.', 'Udzielam moim myślom przyjemnego urlopu.'),
(294, 'Owrzodzenie jamy ustnej', 'owrzodzenie jamy ustnej', 'Zajadłe, gniewne słowa i lęk przed ich wypowiedzeniem.', 'Kocham siebie i dlatego tworzę tylko takie doświadczenia, w których doznaję spokoju. Wszystko jest w porządku.'),
(295, 'Oziębłość', 'ozieblosc', 'Lęk. Wyrzeczenie się przyjemności. Przekonanie, że seks jest zły. Nieczuli partnerzy. Lęk przed ojcem.', 'Bezpiecznie jest cieszyć się własnym ciałem. Cieszę się, że jestem kobietą.'),
(296, 'Padaczka (por epilepsja)', 'padaczka (por epilepsja)', '', ''),
(297, 'Pageta Choroba ', 'pageta choroba ', 'Poczucie utraty gruntu pod nogami. „Nikogo nie obchodzę\".', 'Wiem, że życie wspiera mnie w doskonały i niezrównany sposób. Życie kocha mnie i troszczy się o mnie.'),
(298, 'Palce dłoni', 'palce dloni', 'Odpowiadają poszczególnym sferom życia.', 'Jestem spokojny o szczegóły życia.'),
(299, 'Palce dłoni - Artretyczne', 'palce dloni - artretyczne', 'Chęć karania. Poczucie winy. Przekonanie o byciu ofiarą.', 'Spoglądam z miłością i zrozumieniem. Wszystkie moje doświadczenia rozjaśniam światłem miłości.'),
(300, 'Palce dłoni - Kciuk', 'palce dloni - kciuk', 'Symbolizuje intelekt i troskę.', 'Mój umysł jest spokojny.'),
(301, 'Palce dłoni - Mały', 'palce dloni - maly', 'Symbolizuje rodzinę i udawanie.', 'W rodzinie żyjących istot jestem sobą.'),
(302, 'Palce dłoni - Serdeczny', 'palce dloni - serdeczny', 'Symbolizuje związki z ludźmi i smutek.', 'Spokojnie kocham.'),
(303, 'Palce dłoni - Środkowy', 'palce dloni - srodkowy', 'Symbolizuje gniew i seksualność.', 'Jest mi dobrze z moją seksualnością.'),
(304, 'Palce dłoni - Wskazujący', 'palce dloni - wskazujacy', 'Symbolizuje ego i lęk.', 'Jestem bezpieczny.'),
(305, 'Palce stóp', 'palce stop', 'Reprezentują pomniejsze szczegóły dotyczące przyszłości.', 'Wszystkie szczegóły same się wyjaśnią.'),
(306, 'Palce stóp - Wrastający paznokieć', 'palce stop - wrastajacy paznokiec', 'Troska i poczucie winy związane z należnym prawem, by iść naprzód.', 'Moim Boskim prawem jest wyznaczać własne kierunki w życiu. Jestem bezpieczny. Jestem wolny.'),
(307, 'Palce stóp - Zapalenie kaletki maziowej palucha', 'palce stop - zapalenie kaletki maziowej palucha', 'Brak radości w przeżywaniu życia.', 'Z radością podążam naprzód, by powitać wspaniałe doświadczenia mojego życia.'),
(308, 'Paradontoza', 'paradontoza', 'Gniew z powodu nieumiejętności podjęcia decyzji. Niezdecydowanie.', 'Akceptuję siebie. Moje decyzje są dla mnie zawsze doskonałe.'),
(309, 'Paraliż (por.\nporażenie,\nParkinsona ch.)', 'paraliz (por.\nporazenie,\nparkinsona ch.)', 'Lęk. Paniczny strach. Ucieczka od sytuacji lub osoby. Opór.', 'Stanowię jedność z całością życia. Potrafię się znaleźć w każdej sytuacji.'),
(310, 'Parkinsona choroba(por.\nporażenie, paraliż)', 'parkinsona choroba(por.\nporazenie, paraliz)', 'Lęk i intensywne pragnienie kontrolowania wszystkiego i wszystkich.', 'Rozluźniam się, wiedząc, że jestem bezpieczny. Życie jest dla mnie. Ufam mu.'),
(311, 'Pasożyty', 'pasozyty', 'Przyznawanie mocy tylko innym. Przesadne ustępowanie.', 'Z miłością przyjmuję powracającą do mnie moc i przestaję ulegać wpływom innych.'),
(312, 'Paznokcie', 'paznokcie', 'Symbolizują obronę. W poczuciu bezpieczeństwa wyciągam do innych rękę.', 'W poczuciu bezpieczeństwa wyciągam do innych rękę.'),
(313, 'Paznokcie— obgryzanie', 'paznokcie— obgryzanie', 'Zawód. Zjadanie samego siebie. Uraza wobec jednego z rodziców.', 'Bezpiecznie jest wzrastać i rozwijać się. Kieruję moim własnym życiem z radością i łatwością.'),
(314, 'Pęcherze skórne', 'pecherze skorne', 'Opór. Brak wsparcia emocjonalnego.', 'Łagodnie płynę z życiem i z każdym nowym doświadczeniem. Wszystko jest w porządku.'),
(315, 'Pęcherz moczowy — zapalenie', 'pecherz moczowy — zapalenie', 'Niepokój. Trzymanie się starych poglądów. Lęk przed „wyluzowaniem się\". Poczucie, że jest się lekceważonym.', 'Z łatwością i w spokoju odrzucam stare i witam nowe w moim życiu. Jestem bezpieczny.'),
(316, 'Piersi', 'piersi', 'Symbolizują macierzyństwo, pożywienie i karmienie.', 'Przyjmuję i daję pożywienie w doskonałej równowadze.'),
(317, 'Piersi — dolegliwości', 'piersi — dolegliwosci', 'Odmowa przyjmowania pokarmu. Stawianie wszystkich innych na pierwszym miejscu.', 'Jestem ważna. Liczę się. Dbam o siebie. Odżywiam się z miłością i radością. Pozwalam innym, by byli, kim są. Wszyscy jesteśmy wolni i bezpieczni.'),
(318, 'Piersi — cysty, guzy, bóle', 'piersi — cysty, guzy, bole', 'Przesadne matkowanie. Nadopiekuńczość. Apodyktyczność.', 'Jestem wolny od nadopiekuńczości. Kocham siebie i daję innym przestrzeń do wzrastania.'),
(319, 'Pląsawica (taniec św. Wita)', 'plasawica (taniec sw. wita)', 'Uraza z powodu niemożności zmienienia innych. Brak nadziei.', 'Pozostawiam Wszechświatu wszelką kontrolę. Jestem w pokoju z sobą i z życiem.'),
(320, 'Plecy ', 'plecy ', 'Symbolizują życiowe wsparcie.', 'Wiem, że Życie zawsze mnie wspiera.'),
(321, 'Plecy — dolegliwości (p. rozdział „Zmiany w kręgosłupie\")', 'plecy — dolegliwosci (p. rozdzial „zmiany w kregoslupie\")', '', ''),
(322, 'Plecy - góra', 'plecy - gora', 'Brak emocjonalnego wsparcia. Czucie się niekochanym. Powstrzymywanie miłości.', 'Kocham i akceptuję siebie. Życie kocha mnie i wspiera.'),
(323, 'Plecy — środek', 'plecy — srodek', 'Poczucie winy. Uczucie obarczenia przeszłością.', 'Uwalniam przeszłość. Mogę iść do przodu z miłością w sercu.'),
(324, 'Plecy - dół', 'plecy - dol', 'Lęk o pieniądze. Brak wsparcia finansowego.', 'Ufam biegowi życia. Wszystko, czego potrzebuję, jest zawsze zapewnione. Jestem bezpieczny.'),
(325, 'Pleśniawki por.\ndrożdżyca, usta —\nowrzodzenia', 'plesniawki por.\ndrozdzyca, usta —\nowrzodzenia', 'Złość z powodu podjęcia „błędnych\" decyzji.', 'Przyjmuję moje decyzje z miłością. Wiem, że jestem wolny, by dokonywać zmian. Jestem bezpieczny.'),
(326, 'Płacz', 'placz', 'Łzy są rzeką życia. Płyną zarówno w radości, jak smutku i strachu.', 'Jestem w zgodzie ze swoimi emocjami. Kocham i akceptuję siebie.'),
(327, 'Płciowe organy (por genitalia)', 'plciowe organy (por genitalia)', '', ''),
(328, 'Płuca', 'pluca', 'Zdolność przyjmowania życia.', 'Przyjmuję życie w doskonałej równowadze.'),
(329, 'Płuca - dolegliwości', 'pluca - dolegliwosci', 'Depresja. Zgryzota. Lęk przed życiem. Poczucie, że się nie zasługuje żyć pełnią życia.', 'Potrafię przyjąć pełnię życia. Z miłością żyję życiem w jego najpełniejszym kształcie.'),
(330, 'Płuca— zapalenie', 'pluca— zapalenie', 'Rozpacz. Zmęczenie życiem. Emocjonalne rany, którym nie pozwolono się zagoić.', 'Swobodnie przyjmuję Boskie idee, które wypełnione są tchnieniem i inteligencją Życia. To nowa chwila.'),
(331, 'Pochwa por. kobiece dolegliwości', 'pochwa por. kobiece dolegliwosci', '', ''),
(332, 'Pochwa - zapalenie', 'pochwa - zapalenie', 'Gniew na partnera. Poczucie winy związane z seksem. Karanie siebie.', 'Inni odzwierciedlają miłość i aprobatę, jaką\nmam dla siebie. Cieszę się moją płciowością.'),
(333, 'Pokrzywka', 'pokrzywka', 'Małe, skrywane lęki. Wyolbrzymianie drobnych kłopotów.', 'Wnoszę pokój do każdego zakątka mojego życia.'),
(334, 'Poliomyelitis (ch. Heinego-Medina)', 'poliomyelitis (ch. heinego-medina)', 'Paraliżująca zazdrość. Pragnienie zatrzymania kogoś.', 'Wystarczy dla wszystkich. Tworzę moje dobro i wolność kochającymi myślami.'),
(335, 'Polipy', 'polipy', 'Napięcie i kłótnie w rodzinie. Dziecko czuje się niechciane. Sądzi, że przeszkadza rodzicom.', 'To dziecko jest chciane i kochane.'),
(336, 'Porażenie por. choroba Parkinsona, paraliż', 'porazenie por. choroba parkinsona, paraliz', 'Paraliżujące myśli. Poczucie tkwienia w miejscu.', 'Moje myśli są wolne. Łatwo i z radością przeżywam wspaniałe doświadczenia.'),
(337, 'Poronienie', 'poronienie', 'Lęk. Lęk przed przyszłością. „Nie teraz, później\". Rozmijanie się z upływającym czasem.', 'Moje życie realizuje Boskie prawa. Kocham i akceptuję siebie. Wszystko jest w porządku.'),
(338, 'Poród', 'porod', 'Stanowi wejście w obecny etap historii życia.', 'To dziecko zaczyna teraz nowe, cudowne i radosne życie. Wszystko jest w porządku.'),
(339, 'Poród— uraz okołoporodowy', 'porod— uraz okoloporodowy', 'Przyczyny karmiczne. Wybraliście taki sposób przyjścia na świat. Sami wybieramy naszych rodziców i nasze dzieci. Jest to nieskończony proces.', 'Każde doświadczenie przyczynia się do naszego rozwoju. Jestem pogodzony ze swoją sytuacją.'),
(340, 'Pośladki', 'posladki', 'Symbolizują siłę. Zwiotczałe — utrata sił.', 'Mądrze używam mojej mocy. Jestem silny. Jestem bezpieczny. Wszystko jest w porządku.'),
(341, 'Półpasiec', 'polpasiec', 'Strach. Napięcie. Przewrażliwienie.', 'Jestem rozluźniony i spokojny, ponieważ ufam biegowi życia. Wszystko jest dobrze w moim świecie.'),
(342, 'Prawa strona ciała', 'prawa strona ciala', 'Dawanie, wypuszczanie, energia męska, mężczyzna, ojciec.', 'Lekko i bez wysiłku utrzymuję w równowadze moją męską energię.'),
(343, 'Prostata', 'prostata', 'Symbolizuje męskość.', 'Kocham i cieszę się moją męskością.');
INSERT INTO `afirmacje` (`id`, `problem`, `problemFormat`, `pPrzyczyna`, `nowyWzorzec`) VALUES
(344, 'Prostata — dolegliwości', 'prostata — dolegliwosci', 'Psychiczne lęki osłabiają męskość. Rezygnacja. Seksualne napięcie i poczucie winy. Uczucie starzenia się.', 'Kocham i akceptuję siebie. Akceptuję swoją własną siłę. Na zawsze pozostanę młody duchem.'),
(345, 'Przedmiesiączkowy syndrom', 'przedmiesiaczkowy syndrom', 'Zgoda na rządy chaosu. Uleganie zewnętrznym wpływom. Odrzucanie faktu bycia kobietą.', 'Sama troszczę się o swój umysł i swoje życie. Jestem silną, dynamiczną kobietą. Każda część mojego ciała funkcjonuje doskonale. Kocham siebie.'),
(346, 'Przepuklina', 'przepuklina', 'Zerwane więzi z innymi. Przemęczenie, ciężary. Niewłaściwa ekspresja twórcza.', 'Mój umysł jest łagodny i harmonijny. Kocham i akceptuję siebie. Wolno mi być sobą.'),
(347, 'Przetoka', 'przetoka', 'Lęk. Blokada procesu wydalania.', 'Jestem bezpieczny. Ufam całkowicie procesowi życia. Życie jest dla mnie.'),
(348, 'Przeziębienia por. drogi oddechowe', 'przeziebienia por. drogi oddechowe', 'Zbyt wiele rzeczy naraz. Psychiczny bałagan. Drobne urazy. Przekonanie typu: „Każdej zimy przeziębiam się trzy razy\".', 'Pozwalam umysłowi wypocząć i doznać spokoju. Jasność i harmonia są we mnie i wokół mnie. Wszystko jest w porządku.'),
(349, 'Przysadka mózgowa', 'przysadka mozgowa', 'Symbolizuje ośrodek kontroli.', 'Mój umysł i ciało pozostają w doskonałej równowadze. Kontroluję swoje myśli.'),
(350, 'Psychiczna choroba', 'psychiczna choroba', 'Ucieczka od rodziny. Uchylanie się od życia. Oderwanie się od rzeczywistości.', 'Ten umysł zna swoją prawdziwą tożsamość i jest twórczym przejawem Boskiej Ekspresji.'),
(351, 'Puchlina wodna', 'puchlina wodna', 'Czemu lub komu nie pozwoliłbyś odejść?', 'Świadomie pozbywam się przeszłości. To jest bezpieczne. Jestem wolny.'),
(352, 'Rak', 'rak', 'Głęboka rana. Długotrwała uraza. Głęboki sekret lub żal zżerający od środka. Nienawiść. „Po co to wszystko?\".', 'Z miłością przebaczam i uwalniam się od całej przeszłości. Postanawiam wypełnić mój świat radością. Kocham i akceptuję siebie.'),
(353, 'Rany', 'rany', 'Wściekłość na siebie. Poczucie winy.', 'Wybaczam sobie i postanawiam kochać siebie.'),
(354, 'Rany— cięte por. skaleczenia', 'rany— ciete por. skaleczenia', 'Kara za niestosowanie się do swoich własnych reguł.', 'Tworzę życie, w którym jestem obficie nagradzany.'),
(355, 'Rdzeń kręgowy — zapalenie opon rdzenia kręgowego', 'rdzen kregowy — zapalenie opon rdzenia kregowego', 'Rozpalone myślenie, wściekłość na życie.', 'Mogę poniechać wszelkich zarzutów. Przyjmuję pokój i radość życia.'),
(356, 'Ramiona', 'ramiona', 'Symbolizują zdolność i możliwość przyjmowania życiowych doświadczeń.', 'Z miłością przyjmuję i obejmuję moje doświadczenia. Robię to łatwo i z radością.'),
(357, 'Reumatyzm (gościec)', 'reumatyzm (gosciec)', 'Czucie się ofiarą. Brak miłości. Długotrwałe zgorzknienie. Uraza.', 'To ja kreuję moje doświadczenia. Im bardziej kocham i aprobuję siebie i innych, tym lepsze stają się moje doświadczenia.'),
(358, 'Ręce — nadgarstki', 'rece — nadgarstki', 'Symbolizują ruch i lekkość.', 'Z miłością, mądrze i swobodnie witam moje doświadczenia.'),
(359, 'Ręce — dolegliwości', 'rece — dolegliwosci', 'Złość i napięcie spowodowane przekonaniem, że życie jest niesprawiedliwe.', 'Chcę tworzyć życie, które będzie radosne i bogate. Jestem uspokojony.'),
(360, 'Rogówki zapalenie p. oczy', 'rogowki zapalenie p. oczy', '—', ''),
(361, 'Rozwolnienie por. biegunka', 'rozwolnienie por. biegunka', 'Lęk. Odrzucenie. Ucieczka.', 'Moje przyjmowanie, przyswajanie i wydalanie są w doskonałym porządku. Jestem w zgodzie z życiem.'),
(362, 'Równowagi zaburzenia', 'rownowagi zaburzenia', 'Rozproszone myśli. Brak skupienia.', 'Skupiam się i uznaję doskonałość mojego życia. Wszystko jest w porządku.'),
(363, 'Rozedma płuc', 'rozedma pluc', 'Lęk przed życiem. Nie zasługuję na życie.', 'To moje naturalne prawo, by żyć pełnią życia. Kocham życie. Kocham siebie.'),
(364, 'Rzeżączka por. weneryczne ch.', 'rzezaczka por. weneryczne ch.', 'Poczucie, że jest się złym człowiekiem. Pragnienie kary.', 'Kocham moje ciało. Kocham moją seksualność. Kocham siebie.'),
(365, 'Samobójcze myśli', 'samobojcze mysli', 'Widzenie życia wyłącznie w czarno-białych barwach. Odmowa ujrzenia innych dróg wyjścia.', 'Żyję wśród rozlicznych możliwości. Zawsze jest inne wyjście. Jestem bezpieczny.'),
(366, 'Serce por. krew', 'serce por. krew', 'Reprezentuje ośrodek miłości i bezpieczeństwa.', 'Moje serce bije rytmem miłości.'),
(367, 'Serce - Dolegliwości', 'serce - dolegliwosci', 'Długotrwale problemy emocjonalne. Brak radości. Zatwardziałość serca. Wiara w stres i napięcie.', 'Radość, radość, radość! Z miłością pozwalam radości płynąć przez mój umysł, ciało i doświadczenie.'),
(368, 'Serce - Zawał por. zakrzepica wieńcowa', 'serce - zawal por. zakrzepica wiencowa', 'Wyparcie radości z serca na rzecz pieniędzy, pozycji itp.', 'Sprowadzam radość z powrotem do mojego serca. Wyrażam miłość do wszystkiego.'),
(369, 'Sińce p. stłuczenia', 'since p. stluczenia', '—', '—'),
(370, 'Skaleczenia por. rany', 'skaleczenia por. rany', 'Złość na samego siebie. Poczucie winy.', 'Uwalniam się od gniewu. Kocham i akceptuję siebie.'),
(371, 'Skolioza p. skrzywienie kręgosłupa, garbienie się', 'skolioza p. skrzywienie kregoslupa, garbienie sie', '—', '—'),
(372, 'Skóra', 'skora', 'Ochrania naszą indywidualność. Narząd czucia.', 'Bezpiecznie jest czuć się sobą.'),
(373, 'Skóra — dolegliwości por. wysypka, pokrzywka, łuszczyca', 'skora — dolegliwosci por. wysypka, pokrzywka, luszczyca', 'Niepokój. Lęk. Stare, ukryte rany. Poczucie zagrożenia.', 'Z miłością chronię siebie myślami radości i pokoju. Przeszłość jest przebaczona i zapomniana. Jestem wolny w tej chwili.'),
(374, 'Skręcenie w stawie', 'skrecenie w stawie', 'Gniew i upór. Niechęć do podjęcia kroku w jakimś określonym kierunku.', 'Ufam życiu, które wyniesie mnie na szczyt tego, co we mnie najlepsze. Jestem spokojny.'),
(375, 'Skurcze („spazm\")', 'skurcze („spazm\")', 'Lęk paraliżujący myśli.', 'Rozluźniam się, odprężam i puszczam wszystko. Jestem bezpieczny.'),
(376, 'Skrzywienie kręgosłupa por. garbienie się', 'skrzywienie kregoslupa por. garbienie sie', 'Niezdolność do pójścia wraz ze wspierającym nurtem życia. Lęk, trzymanie się zakorzenionych poglądów.', 'Uwalniam wszystkie obawy. Teraz ufam procesowi życia. Wiem, że życie jest dla mnie. Stoję prosto dzięki sile miłości.'),
(377, 'Splot słoneczny', 'splot sloneczny', 'Wewnętrzne reakcje ciała. Ośrodek naszej intuicji.', 'Ufam mojemu wewnętrznemu głosowi. Mam moc, siłę i mądrość.'),
(378, 'Spojówek zapalenie p. oczy', 'spojowek zapalenie p. oczy', '—', '—'),
(379, 'Srom', 'srom', 'Symbolizuje wrażliwość na zranienie.', 'Bezpiecznie jest być wrażliwą.'),
(380, 'Starzenie się', 'starzenie sie', 'Społeczne przesądy. Zatwardziałe myśli. Lęk przed byciem sobą. Odrzucenie teraźniejszości.', 'Kocham i akceptuję siebie w każdym wieku. Każda chwila życia jest doskonała.'),
(381, 'Starość por. Alzheimera ch.', 'starosc por. alzheimera ch.', 'Wracanie do bezpieczeństwa okresu dziecięcego. Żądanie opieki i uczucia. Pragnienie kontrolowania tych, którzy znajdują się wokół.', 'Boska opieka. Bezpieczeństwo. Spokój. Inteligencja Wszechświata działa w każdej sferze życia.'),
(382, 'Stawy por. artretyzm, łokieć, kolano, barki', 'stawy por. artretyzm, lokiec, kolano, barki', 'Symbolizują zmiany w życiu i łatwość ulegania im.', 'Łatwo poddaję się zmianie. Boskie przewodnictwo w moim życiu sprawia, że zawsze idę w najlepszym kierunku.'),
(383, 'Staw - skokowy', 'staw - skokowy', 'Symbolizują zdolność doświadczania przyjemności. Nieugiętość i poczucie winy.', 'Mam prawo cieszyć się życiem. Akceptuję każdą przyjemność, jaką niesie życie.'),
(384, 'Stawy— zwyrodnienie', 'stawy— zwyrodnienie', 'Opór wobec autorytetów. Poczucie, że jest się wykorzystywanym.', 'Sam dla siebie jestem autorytetem. Kocham i akceptuję siebie. Życie jest dobre.'),
(385, 'Stłuczenia', 'stluczenia', 'Małe, życiowe wstrząsy. Chęć ukarania siebie.', 'Kocham i troszczę się o siebie. Jestem dla siebie życzliwy i łagodny. Wszystko jest w porządku.'),
(386, 'Stopy', 'stopy', 'Symbolizują nasze rozumienie: nas samych, życia, innych ludzi.', 'Moje rozumienie jest jasne. Chcę się zmieniać wraz z upływem czasu. Jestem bezpieczny.'),
(387, 'Stopy — dolegliwości', 'stopy — dolegliwosci', 'Strach przed przyszłością.', 'Kroczę do przodu z radością i łatwością.'),
(388, 'Stopy — grzybica', 'stopy — grzybica', 'Rozczarowanie wobec braku akceptacji. Niezdolność postępowania lekko do przodu.', 'Kocham i aprobuję siebie. Daję sobie pozwolenie na pójście do przodu. Jest bezpiecznie podążać naprzód.'),
(389, 'Stopy — poduszki zapalenie', 'stopy — poduszki zapalenie', 'Brak radości w doświadczaniu życia.', 'Radośnie wybiegam naprzód, by powitać cudowne przeżycia.'),
(390, 'Stwardnienie rozsiane (SM — sclerosis multiplex)', 'stwardnienie rozsiane (sm — sclerosis multiplex)', 'Zatwardziałość serca i umysłu. Żelazna wola, brak elastyczności. Lęk.', 'Wybierając pełne miłości, radosne myśli tworzę kochający, radosny świat. Jestem bezpieczny i wolny.'),
(391, 'Swędzenie', 'swedzenie', 'Pragnienia niezgodne z własnym usposobieniem. Brak satysfakcji. Wyrzuty sumienia.', 'Jestem spokojny tu, gdzie jestem. Akceptuję moje dobro, wiedząc, że wszystkie moje potrzeby i pragnienia będą spełnione.'),
(392, 'Szczęki — dolegliwości', 'szczeki — dolegliwosci', 'Złość. Uraza. Chęć zemsty.', 'Chcę zmienić sposób myślenia, który spowodował taką sytuację. Kocham i akceptuję siebie. Jestem bezpieczny.'),
(393, 'Szczękościsk', 'szczekoscisk', 'Złość. Chęć kontrolowania. Odmowa wyrażania uczuć.', 'Ufam życiu. Z łatwością proszę o to, czego chcę. Życie mnie wspiera.'),
(394, 'Szkielet por. kości', 'szkielet por. kosci', 'Kości symbolizują strukturę życia. Choroby kości — załamanie życiowej konstrukcji.', 'Jestem silny i zdrowy. Jestem dobrze skonstruowany.'),
(395, 'Szpik kostny', 'szpik kostny', 'Symbolizuje najgłębsze przekonania o samym sobie. Sposób, w jaki wspomagasz i dbasz o siebie.', 'Podstawą mojego życia jest Boski Duch. Jestem bezpieczny, kochany i zawsze wspierany.'),
(396, 'Sztywność karku', 'sztywnosc karku', 'Nieugięty upór.', 'Bezpiecznie jest poznawać inne sposoby myślenia.'),
(397, 'Sztywność mięśni', 'sztywnosc miesni', 'Sztywne, oporne myślenie.', 'Jestem wystarczająco bezpieczny, aby zachować elastyczność umysłu.'),
(398, 'Szyja', 'szyja', 'Symbolizuje elastyczność. Zdolność patrzenia z różnych punktów widzenia.', 'Jestem w zgodzie z życiem.'),
(399, 'Szyja — dolegliwości por. „Zmiany w kręgosłupie\", sztywność karku', 'szyja — dolegliwosci por. „zmiany w kregoslupie\", sztywnosc karku', 'Odmowa uwzględnienia innych aspektów zagadnienia. Upór, brak elastyczności.', 'Elastyczność pozwala mi dostrzegać wszystkie strony zagadnienia. Jest nieskończenie wiele sposobów widzenia i robienia czegoś. Jestem bezpieczny.'),
(400, 'Śledziona', 'sledziona', 'Obsesje. Natrętne myśli.', 'Kocham i akceptuję siebie. Wierzę, że życie jest dla mnie. Jestem bezpieczny. Wszystko jest w porządku.'),
(401, 'Śmierć', 'smierc', 'Symbolizuje odejście z obecnego etapu życia.', 'Z radością zmierzam ku innym obszarom doświadczania. Wszystko jest w porządku.'),
(402, 'Śpiączka', 'spiaczka', 'Lęk. Ucieczka przed czymś lub kimś.', 'Otaczamy cię bezpieczeństwem i miłością. Tworzymy przestrzeń, w której wyzdrowiejesz. Jesteś kochany.'),
(403, 'Świerzb', 'swierzb', '„Skażone\" myślenie. Zgoda na to, aby inni załazili nam za skórę.', 'Jestem czującym, kochającym, radosnym wyrazem życia. Jestem sobą.'),
(404, 'Tarczyca por. nadczynność, niedoczynność tarczycy, wole', 'tarczyca por. nadczynnosc, niedoczynnosc tarczycy, wole', 'Upokorzenie. „Nigdy nie mogę się doczekać, żeby robić to, co chcę robić\". „Kiedy wreszcie będzie moja kolej?\"', 'Wychodzę poza stare ograniczenia i pozwalam sobie na wyrażanie siebie swobodnie i twórczo.'),
(405, 'Tasiemczyca', 'tasiemczyca', 'Silne przekonanie, że jest się ofiarą i kimś nieczystym. Bezradność.', 'Inni ludzie są odbiciem wyłącznie dobrych uczuć, jakie mam w stosunku do siebie. Kocham i akceptuję siebie takim, jakim jestem.'),
(406, 'Tętnice', 'tetnice', 'Niosą radość życia.', 'Jestem przepełniony radością. Radość przepływa przeze mnie z każdym uderzeniem mojego serca.'),
(407, 'Tężec por. szczękościsk', 'tezec por. szczekoscisk', 'Potrzeba wyładowania złych, jątrzących myśli.', 'Pozwalam miłości wypływającej z mego serca przemyć, oczyścić i uzdrowić każdy fragment mego ciała i moich uczuć.'),
(408, 'Tiki', 'tiki', 'Lęk. Uczucie, że jest się obserwowanym.', 'Jestem akceptowany przez Życie. Wszystko jest w porządku. Jestem bezpieczny.'),
(409, 'Tłuszczowa tkanka por. nadwaga', 'tluszczowa tkanka por. nadwaga', 'Nadwrażliwość. Często symbolizuje lęk i wyraża potrzebę ochrony. Lęk może być maskowany gniewem i silną niechęcią do wybaczania.', 'Jestem chroniony przez Boską Miłość. Jestem zawsze bezpieczny. Chcę wzrastać i brać odpowiedzialność za swoje życie. Wybaczam innym i sam tworzę swoje życie. Jestem bezpieczny.'),
(410, 'Tłuszczowa tkanka — biodra', 'tluszczowa tkanka — biodra', 'Uparty gniew na rodziców.', 'Chcę wybaczyć całą przeszłość. Bezpiecznie jest wyrastać poza ograniczenia rodziców.'),
(411, 'Tłuszczowa tkanka — brzuch', 'tluszczowa tkanka — brzuch', 'Gniew z powodu nieotrzymania jedzenia lub oparcia.', 'Żywię się sam pokarmem duchowym. Jestem syty i wolny.'),
(412, 'Tłuszczowa tkanka — ramiona', 'tluszczowa tkanka — ramiona', 'Gniew z powodu nieotrzymania miłości.', 'Jestem bezpieczny, kiedy sam kreuję całą miłość, której potrzebuję.'),
(413, 'Tłuszczowa tkanka — uda', 'tluszczowa tkanka — uda', 'Nagromadzona złość z dzieciństwa. Często gniew na ojca.', 'Widzę mojego ojca jako niekochane dziecko i z łatwością mu wybaczam. Oboje jesteśmy wolni.'),
(414, 'Toczeń rumieniowaty (liszaj)', 'toczen rumieniowaty (liszaj)', 'Rezygnacja. Lepiej umrzeć, niż wesprzeć samego siebie. Gniew. Karanie siebie.', 'Mówię w swoim imieniu z łatwością i swobodnie. Znam swoją własną siłę. Kocham i akceptuję siebie. Jestem wolny i bezpieczny.'),
(415, 'Trąd', 'trad', 'Niezdolność radzenia sobie w życiu. Długotrwałe przekonanie, że nie jest się dość dobrym lub dość czystym.', 'Przekraczam wszelkie ograniczenia. Jestem prowadzony i inspirowany Boskością. Miłość leczy wszystko.'),
(416, 'Trądzik', 'tradzik', 'Nieakceptowanie siebie. Nielubienie siebie.', 'Jestem Boskim wyrazem życia. Kocham i akceptuję siebie tu i teraz.'),
(417, 'Trzustka', 'trzustka', 'Symbolizuje słodycz życia.', 'Moje życie jest pełne słodyczy.'),
(418, 'Trzustka— stan zapalny', 'trzustka— stan zapalny', 'Odrzucenie. Gniew i frustracja, ponieważ życie utraciło swoją słodycz.', 'Kocham i akceptuję siebie. To ja sam tworzę słodycz i radość mojego życia.'),
(419, 'Twardzina skóry', 'twardzina skory', 'Obrona przed życiem. Brak zaufania do siebie.', 'Rozluźniam się całkowicie, bo wiem, że jestem bezpieczny. Ufam Życiu i ufam sobie.'),
(420, 'Twarz', 'twarz', 'Symbolizuje to, co okazujemy światu.', 'Bezpiecznie jest być sobą. Wyrażam to, kim jestem.'),
(421, 'Twarz — obwisłe rysy twarzy', 'twarz — obwisle rysy twarzy', 'Są skutkiem „wisielczych\" myśli. Niechęć do życia.', 'Wyrażam radość życia i cieszę się każdą chwilą. Jestem znów młody.'),
(422, 'Udar mózgu', 'udar mozgu', 'Rezygnacja. Opór. „Raczej umrę, niż zmienię\". Odrzucenie życia.', 'Życie jest ciągłą zmianą i łatwo przystosowuję się do nowości. Akceptuję życie — przeszłość, teraźniejszość i przyszłość.'),
(423, 'Ukąszenia', 'ukaszenia', 'Lęk. Nadwrażliwość na każde uchybienie ze strony innych.', 'Wybaczam sobie. Kocham siebie teraz i zawsze.'),
(424, 'Ukąszenia — przez owady', 'ukaszenia — przez owady', 'Poczucie winy z powodu drobiazgów.', 'Uwalniam się od rozdrażnienia. Wszystko jest w porządku.'),
(425, 'Ukąszenia — przez zwierzęta', 'ukaszenia — przez zwierzeta', 'Gniew skierowany do wewnątrz. Pragnienie kary.', 'Jestem wolny.'),
(426, 'Układ krążenia', 'uklad krazenia', 'Symbolizuje zdolność do pozytywnego doświadczania i wyrażania emocji.', 'Jestem wolny. Napełniam cały świat miłością i radością. Kocham życie.'),
(427, 'Upławy por. kobiece dolegliwości, pochwa — zapalenie', 'uplawy por. kobiece dolegliwosci, pochwa — zapalenie', 'Przeświadczenie, że kobiety są bezsilne wobec przeciwnej płci. Gniew na swojego partnera.', 'To ja tworzę swoje przeżycia. Ja jestem siłą. Cieszę się z mojej kobiecości. Jestem wolna.'),
(428, 'Usta', 'usta', 'Reprezentują przyjmowanie nowych idei i pokarmu.', 'Karmię się miłością.'),
(429, 'Usta — dolegliwości', 'usta — dolegliwosci', 'Ustalone opinie. Ograniczony umysł. Niezdolność przyjmowania nowych idei.', 'Z chęcią przyjmuję nowe idee i koncepcje. Przygotowuję je do przemyślenia i przyswojenia.'),
(430, 'Usta — owrzodzenia zgorzelinowe', 'usta — owrzodzenia zgorzelinowe', 'Jątrzące, ale niewypowiedziane słowa. Wyrzuty.', 'W moim kochającym świecie tworzę tylko radosne przeżycia.'),
(431, 'Uszy', 'uszy', 'Symbolizują zdolność słuchania.', 'Słucham z miłością.'),
(432, 'Uszy — szum (dzwonienie)', 'uszy — szum (dzwonienie)', 'Odmowa słuchania. Upór. Niesłyszenie wewnętrznego głosu.', 'Ufam mojemu wyższemu Ja. Słucham z miłością mojego wewnętrznego głosu. Uwalniam się od wszystkiego, co niepotrzebne miłości.'),
(433, 'Uszy — ból', 'uszy — bol', 'Gniew. Odmowa słuchania. Zbyt wiele zamieszania. Kłótnie rodziców.', 'Otacza mnie harmonia. Słucham z miłością tego, co jest dobre i przyjemne. Jestem centrum miłości.'),
(434, 'Uszy — zapalenie wyrostka sutkowego', 'uszy — zapalenie wyrostka sutkowego', 'Gniew i zawód. Pragnienie, by nie słyszeć, co się dzieje (zwykle u dzieci). Lęk paraliżujący zrozumienie.', 'Boski spokój i zgoda otaczają mnie i mieszkają we mnie. Jestem oazą pokoju i miłosnej radości. Wszystko jest dobrze w moim świecie.'),
(435, 'Wągry', 'wagry', 'Drobne wybuchy gniewu.', 'Uciszam moje myśli i jestem opanowany.'),
(436, 'Wątroba', 'watroba', 'Siedlisko gniewu i prymitywnych emocji.', 'To, co znam, jest miłością, radością i pokojem.'),
(437, 'Wątroba— dolegliwości por. żółtaczka', 'watroba— dolegliwosci por. zoltaczka', 'Bezustanne uskarżanie się. Oszukiwanie siebie przez ciągłe obwinianie. Przekonanie, że jest się złym.', 'Chcę życie przyjmować otwartym sercem. Szukam miłości i znajduję ją wszędzie.'),
(438, 'Wątroba — zapalenie', 'watroba — zapalenie', 'Opór na zmianę. Strach, gniew, nienawiść. Wątroba jako siedlisko gniewu, wściekłości.', 'Mój umysł jest oczyszczony i wolny. Zostawiam przeszłość i wkraczam w nowe. Wszystko jest w porządku.'),
(439, 'Weneryczne choroby por. AIDS, opryszczka płciowa, rzeżączka, kiła', 'weneryczne choroby por. aids, opryszczka plciowa, rzezaczka, kila', 'Poczucie seksualnej winy. Przeświadczenie, że narządy płciowe są grzeszne i nieczyste. Pragnienie kary.', 'Z miłością i radością uznaję moją seksualność i wyrażanie jej. Dopuszczam do siebie tylko takie myśli, które wspierają mnie i sprawiają, że czuję się dobrze.'),
(440, 'Wirusowa infekcja por. infekcje', 'wirusowa infekcja por. infekcje', 'Brak radości w życiu. Zgorzknienie.', 'Z miłością dopuszczam obfitość radości do mojego życia. Kocham siebie.'),
(441, 'Włosy — nadmiar owłosienia', 'wlosy — nadmiar owlosienia', 'Ukryty gniew. Zwykle pod maską lęku. Potrzeba potępiania. Często niechęć do karmienia siebie.', 'Jestem dla siebie kochającym rodzicem. Jestem osłaniany miłością i aprobatą. Bezpiecznie jest pokazać kim jestem.'),
(442, 'Włosy — siwienie', 'wlosy — siwienie', 'Stres. Wiara w presję i wysiłek.', 'Jestem spokojny i dobrze mi w każdej sferze życia. Jestem silny i pełen możliwości.'),
(443, 'Włosy — wypadanie', 'wlosy — wypadanie', 'Strach, napięcie. Próba kontrolowania wszystkiego. Nieufność wobec biegu życia.', 'Jestem bezpieczny. Kocham i aprobuję siebie. Ufam życiu.'),
(444, 'Włókniaki', 'wlokniaki', 'Silne przekonanie, że wszystko w życiu dzieje się na moją niekorzyść. „Jaki jestem biedny\".', 'Życie kocha mnie i ja kocham życie. Chcę przeżywać życie swobodnie i w całej pełni.'),
(445, 'Wole', 'wole', 'Nienawiść z powodu przymusu. Poczucie bycia ofiarą. Wszystko w życiu dzieje się na przekór. Niespełnienie.', 'Jestem siłą i autorytetem w swoim życiu. Wolno mi być sobą.'),
(446, 'Wrzody', 'wrzody', 'Roztrząsanie doznanych przykrości i lekceważenia. Pragnienie odwetu.', 'Pozwalam swoim myślom być wolnymi. Przeszłość jest zakończona. Jestem spokojny.'),
(447, 'Wrzody trawienne por. wrzód żołądka, żołądek — dolegliwości, zgaga', 'wrzody trawienne por. wrzod zoladka, zoladek — dolegliwosci, zgaga', 'Strach. Uczucie, że nie jest się dość dobrym. Co cię zżera?', 'Kocham i akceptuję siebie. Jestem w zgodzie z sobą. Jestem spokojny. Wszystko jest w porządku.'),
(448, 'Wrzód żołądka por. wrzody trawienne, żołądek — dolegliwości, zgaga', 'wrzod zoladka por. wrzody trawienne, zoladek — dolegliwosci, zgaga', 'Strach. Przekonanie, że nie jest się dość dobrym. Chęć przypodobania się.', 'Kocham i aprobuję siebie. Jestem pogodny ze sobą. Jestem cudowny.'),
(449, 'Wścieklizna', 'wscieklizna', 'Gniew. Przekonanie, że przemoc jest jedynym rozwiązaniem.', 'Spokój otacza mnie i mieszka we mnie.'),
(450, 'Wymioty', 'wymioty', 'Gwałtowne odrzucanie nowych rozwiązań. Lęk przed tym, co nowe.', 'Z radością i w poczuciu bezpieczeństwa chłonę życie. Tylko dobro przychodzi do mnie i przechodzi przeze mnie.'),
(451, 'Wypadki — podatność', 'wypadki — podatnosc', 'Niezdolność do przemawiania w swoim imieniu. Bunt przeciwko autorytetom. Wiara w przemoc.', 'Uwalniam się od wzorca, który stworzył taką sytuację. Jestem spokojny. Jestem wart starań.'),
(452, 'Wypryski ropne por. wągry', 'wypryski ropne por. wagry', 'Wstyd. Wiara w brzydotę.', 'Akceptuję siebie. Jestem piękny i kochany.'),
(453, 'Wyrostek robaczkowy — zapalenie', 'wyrostek robaczkowy — zapalenie', 'Lęk. Lęk przed życiem. Blokowanie przepływu dobra.', 'Jestem bezpieczny. Rozluźniam się i pozwalam radośnie płynąć życiu.'),
(454, 'Wysypka por. pokrzywka, skóra', 'wysypka por. pokrzywka, skora', 'Złość z powodu zwlekania. Dziecinne sposoby przyciągania uwagi.', 'Kocham i akceptuję siebie. Jestem w zgodzie z życiem.'),
(455, 'Wzdęcia, gazy', 'wzdecia, gazy', 'Zamykanie się. Lęk. Chaotyczne myśli.', 'Odprężam się i pozwalam życiu swobodnie płynąć przeze mnie.'),
(456, 'Zadrapania', 'zadrapania', 'Poczucie, że jest się rozdzieranym przez życie, że życie jest nieetyczne. Uczucie rozdarcia.', 'Jestem wdzięczny za wspaniałomyślność życia. Życie mi błogosławi.'),
(457, 'Zakrzepica wieńcowa, por. serce — zawał', 'zakrzepica wiencowa, por. serce — zawal', 'Uczucie osamotnienia i przerażenia. „Nie jestem dość dobry. Nie robię tyle, na ile mnie stać. Nigdy tego nie zrobię\".', 'Stanowię jedność z całością życia. Wszechświat wspiera mnie całkowicie. Wszystko jest w porządku.'),
(458, 'Zanik pamięci, demencja por. Alzheimera ch., starość', 'zanik pamieci, demencja por. alzheimera ch., starosc', 'Odmowa traktowania świata takim, jakim jest. Brak nadziei, gniew.', 'Jestem we właściwym miejscu. Jestem zawsze bezpieczny.'),
(459, 'Zapalenie (stan zapalny) por. zapalenie typu — itis', 'zapalenie (stan zapalny) por. zapalenie typu — itis', 'Lęk. Gotowanie się ze złości. Jątrzące myśli.', 'Moje myślenie jest spokojne, opanowane i skupione.'),
(460, 'Zapalenie typu itis por. zapalenie', 'zapalenie typu itis por. zapalenie', 'Gniew i rozczarowanie tym, co widzisz w swoim życiu.', 'Odrzucam wszystkie wzorce, które powodowały moją krytyczną postawę. Kocham i akceptuję siebie.'),
(461, 'Zaparcie', 'zaparcie', 'Niechęć do wyzbycia się starych przekonań. Tkwienie w przeszłości. Czasem skąpstwo.', 'Kiedy odrzucam przeszłość, przychodzi nowe, świeże i pełne życia. Pozwalam życiu przepływać przeze mnie.'),
(462, 'Zatoki', 'zatoki', 'Irytacja na jakąś osobę, kogoś bliskiego.', 'Ogłaszam, że pokój i harmonia zamieszkują we mnie i otaczają mnie przez cały czas. Wszystko jest w porządku.'),
(463, 'Zatrucie pokarmowe', 'zatrucie pokarmowe', 'Pozostawienie kontroli innym. Bezbronność.', 'Jestem wystarczająco silny i sprawny, by przyswoić wszystko, co napotkam.'),
(464, 'Zatrzymywanie płynów por. opuchlina, puchlina wodna', 'zatrzymywanie plynow por. opuchlina, puchlina wodna', 'Co obawiasz się utracić? Z radością uwalniam się od tego, co zbędne.', 'Z radością uwalniam się od tego, co zbędne.'),
(465, 'Zęby', 'zeby', 'Odpowiadają procesowi podejmowania decyzji.', 'Moje decyzje opierają się na zasadach prawdy. Czuję się bezpieczny, wiedząc, iż tylko właściwe działanie ma miejsce w moim życiu.'),
(466, 'Zęby — dolegliwości por. kanał korzenia zębowego', 'zeby — dolegliwosci por. kanal korzenia zebowego', 'Długotrwałe niezdecydowanie. Niezdolność przełamania się, by zanalizować myśli i podjąć decyzję.', 'Buduję mocną podstawę dla siebie i mojego życia. Sam wybieram swoje przekonania, by radośnie wspierać siebie.'),
(467, 'Zęby — kanał korzenia zębowego', 'zeby — kanal korzenia zebowego', 'Niemożność wgryzienia się w cokolwiek. Ruina podstawowych przekonań.', 'Buduję mocną podstawę dla siebie i mojego życia. Sam wybieram swoje przekonania, by radośnie wspierać siebie.'),
(468, 'Zęby — wklinowany ząb mądrości', 'zeby — wklinowany zab madrosci', 'Niedawanie sobie psychicznej przestrzeni na to, by stworzyć solidny fundament.', 'Otwieram swoją świadomość na ekspansję życia. Jest dość miejsca, bym rósł i zmieniał się.'),
(469, 'Zgaga por. wrzody trawienne, wrzód żołądka, żołądek — dolegliwości', 'zgaga por. wrzody trawienne, wrzod zoladka, zoladek — dolegliwosci', 'Lęk. Lęk. Lęk. Paraliżujący lęk.', 'Oddycham swobodnie i głęboko. Jestem bezpieczny. Ufam życiu.'),
(470, 'Złamanie p. kości', 'zlamanie p. kosci', 'Zmęczenie. Opór, nuda. Brak miłości do tego, co się robi.', 'Życie napawa mnie entuzjazmem i wypełnia energią i zapałem.'),
(471, 'Żarłoczność', 'zarlocznosc', 'Beznadziejna rozpacz. Obłędne napychanie i oczyszczanie się z nienawiści do siebie.', 'Samo życie mnie kocha, karmi i wspiera. Bezpiecznie jest żyć.'),
(472, 'Żołądek', 'zoladek', 'Przyjmuje pokarm. Przyswaja idee.', 'Przyswajam życie z łatwością.'),
(473, 'Żołądek — dolegliwości por. wrzody, wrzód żołądka, zgaga', 'zoladek — dolegliwosci por. wrzody, wrzod zoladka, zgaga', 'Przerażenie. Lęk przed nowością. Niezdolność do jej przyswojenia.', 'Życie jest ze mną w zgodzie. Przyjmuję nowe w każdej chwili dnia. Wszystko w porządku.'),
(474, 'Żołądek — nieżyt por. żołądek — dolegliwości', 'zoladek — niezyt por. zoladek — dolegliwosci', 'Przedłużająca się niepewność. Oczekiwanie czegoś najgorszego.', 'Kocham i akceptuję siebie. Jestem bezpieczny.'),
(475, 'Żółtaczka por. wątroba — dolegliwości', 'zoltaczka por. watroba — dolegliwosci', 'Uprzedzenia do tego, co wewnątrz i na zewnątrz. Niezrównoważenie.', 'Jestem tolerancyjny, pełen współczucia i miłości wobec wszystkich ludzi, łącznie ze mną.'),
(476, 'Żuchwa — problemy', 'zuchwa — problemy', 'Gniew, uraza, żal, pragnienie zemsty. Chcę zmienić wzorce, które stworzyły ten stan.', 'Kocham i aprobuję siebie. Jestem bezpieczny.'),
(477, 'Żylaki', 'zylaki', 'Pozostawanie w sytuacji, której nienawidzisz. Zniechęcenie. Czucie się przeciążonym i przepracowanym.', 'Trwam w prawdzie i żyję i poruszam się w radości. Kocham Życie i poruszam się swobodnie.'),
(478, 'Żył zapalenie', 'zyl zapalenie', 'Gniew i frustracja. Obwinianie innych za ograniczenia i brak radości w życiu.', 'Teraz radość swobodnie przepływa przeze mnie. Jestem w zgodzie z życiem.');
